import styled from "styled-components";
import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function SectionProject() {
  const sliders = [
    { id: 0, text: "KEEP GOING" },
    { id: 1, text: "KEEP GOING" },
    { id: 2, text: "KEEP GOING" },
    { id: 3, text: "KEEP GOING" },
    { id: 4, text: "KEEP GOING" },
  ];

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    autoplay: true,
    slidesToShow: 1,
    slidesToScroll: 3,
    arrows: false,
  };

  return (
    <Container>
      <Slider {...settings}>
        {sliders.map(({ text }) => {
          return (
            <TextBannerWrap>
              <TextBannerk key={item.id}>{text}</TextBannerk>
            </TextBannerWrap>
          );
        })}
      </Slider>
    </Container>
  );
}

export default SectionProject;

const Container = styled.div`
  width: 100%;
`;

const StyledSlider = styled(Slider)`
  .slick-list {
    width: 1600px;
    margin: 0 auto;
  }
`;

const TextBannerWrap = styled.div`
  width: 100%;

  background-color: red;
`;

const TextBanner = styled.h3`
  padding: 20px;
  font-weight: ${({ theme }) => theme.fonts.weightExtra};
  font-size: 110px;
  color: #000;
`;
