import styled from "styled-components";

function SectionAbout() {
  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <ViewBtn>
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </ViewBtn>
      </Title>
      <Wrap>
        <SlideView>
          <SlideWrap>
            <li>
              <ProfileWrap>
                <Left>
                  <h2>PROFILE</h2>
                </Left>
                <Right>
                  <ul>
                    <li>
                      <h4>name</h4>
                      <p>
                        Kim Ah Reum 김아름
                        <span></span>
                      </p>
                    </li>
                    <li>
                      <h4>address</h4>
                      <p>
                        서울특별시 마포구 성산동
                        <span></span>
                      </p>
                    </li>
                    <li>
                      <h4>birth</h4>
                      <p>
                        1989.03.07
                        <span></span>
                      </p>
                    </li>
                    <li>
                      <h4>phone number</h4>
                      <p>010.2967.5037</p>
                    </li>
                    <li>
                      <h4>e-mail</h4>
                      <p>
                        aarreum0307@gmail.com
                        <span></span>
                      </p>
                    </li>
                  </ul>
                </Right>
              </ProfileWrap>
            </li>
