import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
function SectionProject() {
  const data = [
    {
      id: 0,
      title: "MOVIELIST",
      btn: "01.MOVIELIST",
      content: "https://kimahr.github.io/movie-list/",

      dots: "01",
    },
    {
      id: 1,
      title: "TODOLIST",
      btn: "02.TODOLIST",
      content: "https://kimahr.github.io/todolist/",

      dots: "02",
    },
    {
      id: 2,
      title: "PORTFOLIO",
      btn: "03.PORTFOLIO",
      content: "https://kimahr.github.io/instagram/",

      dots: "03",
    },
  ];

  const delay = 2500;
  const [index, setIndex] = useState(0);

  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

    return () => {
      resetTimeout();
    };
  });
  return (
    <Container id="project">
      <TitleWrap>
        <Title>
          <h4>04</h4>
          <h2>learn react</h2>
        </Title>
        <TitleBox>
          <h3>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Quaerat nesciunt, unde vel, ipsum nulla quos alias quidem velit vero est sed. Voluptate nihil distinctio maiores architecto
            corrupti, quam suscipit laudantium!
          </h3>
        </TitleBox>
      </TitleWrap>
      <Wrap>
        <BtnWrap>
          {data.map((item) => (
            <BtnList key={item.id} className={index === item.id ? SlideWrap.active : null} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)` }}>
              <BtnTxt>{item.btn}</BtnTxt>
              {data.map((index) => (
                <div key={index}></div>
              ))}
            </BtnList>
          ))}
        </BtnWrap>
        <Slide>
          <SlideWrap>
            {data
              .filter((item) => index === item.id)
              .map((item) => (
                <SlideList>
                  <SlideTxt>
                    <a href="https://kimahr.github.io/movie-list/">{item.title}</a>
                    <span></span>
                  </SlideTxt>

                  <OneWrap>
                    {data.map((item) => (
                      <OneList
                        key={item.id}
                        className={index === item.id ? SlideWrap.active : null}
                        onClick={() => setIndex(item.id)}
                        style={{ transform: `translate3d(${index}%)`, transition: "all 3s" }}
                      >
                        <One key={item.id} className={index === item.id ? SlideWrap.active : null} style={index === item.id ? { color: "#fff" } : { color: "#000" }}>
                          {item.dots}
                        </One>
                        {data.map((index) => (
                          <div key={index}></div>
                        ))}
                      </OneList>
                    ))}
                  </OneWrap>
                </SlideList>
              ))}
          </SlideWrap>
        </Slide>
      </Wrap>
    </Container>
  );
}

