import styled from "styled-components";

function SideMenu({ setMenuOpen }) {
  const closeMenu = () => {
    setMenuOpen(false);
  };
  return (
    <Mbtn onClick={closeMenu}>
      <Wrap>
        <LineWrap>
          <a href="#">
            <span>
              <i></i>
              <i></i>
              <i></i>
            </span>
          </a>
        </LineWrap>
        <ul>
          <li>
            <Num>01</Num>
            <div class="txtWrap">
              <a href="./index.php" class="mainBtn_m">
                ABOUT
              </a>
              <span></span>
            </div>
          </li>
          <li class="addNav_m nav_m1">
            <Num>01</Num>
            <div class="txtWrap">
              <a href="./sub/design_uiux.php" class="mainBtn_m">
                SKILLS
              </a>
              <span></span>
            </div>
          </li>
          <li>
            <Num>01</Num>
            <div class="txtWrap">
              <a href="./sub/develop.php" class="mainBtn_m">
                DEVELOP
              </a>
              <span></span>
            </div>
          </li>
          <li>
            <Num>01</Num>
            <div class="txtWrap">
              <a href="./sub/about.php" class="mainBtn_m">
                PROJECT
              </a>
              <span></span>
            </div>
          </li>
        </ul>
      </Wrap>
    </Mbtn>
  );
}

const Mbtn = styled.div`
  z-index: 5;
  display: block;
  position: fixed;
  top: 0;
  left: 100%;
  background: #0c1017;
  width: 100%;
  height: 100%;
  transition: all 0.3s;
  overflow: hidden;
`;

const Wrap = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;

  li {
    width: 100%;
    display: flex;
    justify-content: space-around;
  }
`;

