import styled from "styled-components";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Box = styled.div`
  position: relative;
  width: 100%;
  height: 100%;

  a {
    z-index: 2;
    width: 100%;
    margin: 0 auto;
    position: relative;

    font-weight: 700;
    color: #000;
    font-size: 50px;
    transition: all 0.4s;

    &::before {
      z-index: -1;
      content: "";
      position: absolute;
      left: 0%;
      top: 100%;

      height: 20px;
      box-shadow: inset 0px 0px 0px #0066ff;
      background: #5053582c;

      transition: all 0.4s cubic-bezier(0.5, 0.24, 0, 1);
    }
    &:hover {
      &::before {
        box-shadow: inset 650px 0px 0px #0066ff;
      }
    }
  }
`;

const StyleSlider = styled(Slider)`
  .slick-slide {
    width: 100%;
    height: 300px;
  }
  .slick-slide div {
    outline: none;
    /* background-color: orange; */
    width: 50%;
    margin: 0 auto;
  }
  .slick-slide div a {
    font-size: 50px;
    margin: 0 auto;
    z-index: 2;

    font-weight: 700;
    color: #000;
    position: absolute;

    display: block;
    width: 100%;

    list-style: none;
    text-align: center;
  }
  .slick-slide div a ::before {
    z-index: -1;
    content: "";
    position: absolute;
    left: 0%;
    top: 70%;
    width: 50%;
    height: 20px;
    box-shadow: inset 0px 0px 0px #0066ff;
    background: #5053582c;

    transition: all 0.4s cubic-bezier(0.5, 0.24, 0, 1);
  }
`;
export { StyleSlider, Box };
