import styled from "styled-components";
import { useState, useEffect, useRef } from "react";

function SectionAbout() {
  const data = [
    {
      id: 0,
      title: "sociable",
      content: "sociable",
      contents: "사교적인",
      dots: "",
    },
    {
      id: 1,
      title: "enterprising",
      content: "enterprising",
      contents: "진취력있는",
      dots: "",
    },
    {
      id: 2,
      title: "shrewd",
      content: "shrewd",
      contents: "catch on fast",
      dots: "",
    },
    {
      id: 3,
      title: "sociable",
      content: "sociable",
      contents: "사교적인",
      dots: "",
    },
    {
      id: 4,
      title: "considerate",
      content: "considerate",
      contents: "배려하는",
      dots: "",
    },
    {
      id: 5,
      title: "hardworking",
      content: "hardworking",
      contents: "성실한",
      dots: "",
    },
    {
      id: 6,
      title: "figma",
      content: "figma",
      contents: "김아름",
      dots: "",
    },
    {
      id: 7,
      title: "responsibility",
      content: "responsibility",
      contents: "책임감",
      dots: "",
    },
  ];

  const delay = 2500;
  const [index, setIndex] = useState(0);

  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

    return () => {
      resetTimeout();
    };
  });
  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>skills</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
      </Title>
      <Wrap>
        <Slide>
          <SlideWrap>
            {data
              .filter((item) => index === item.id)
              .map((item) => (
                <SlideList>
                  <a href="#main">{item.content}</a>
                  <span></span>
                  <h4>{item.contents}</h4>

                  <OneWrap>
                    {data.map((item) => (
                      <OneList
                        key={item.id}
                        className={index === item.id ? SlideWrap.active : null}
                        onClick={() => setIndex(item.id)}
                        style={{ transform: `translate3d(${index}%)`, transition: "all 3s" }}
                      >
                        <One key={item.id} className={index === item.id ? SlideWrap.active : null} style={index === item.id ? { background: "#000" } : { background: "#8e8f9a" }}>
                          {item.dots}
                        </One>
                        {data.map((index) => (
                          <div key={index}></div>
                        ))}
                      </OneList>
                    ))}
                  </OneWrap>
                </SlideList>
              ))}
          </SlideWrap>
        </Slide>
      </Wrap>
      <BgWrap>
        <Bg>
          <TextWrap>
            <Txt>
              <h3>EDUCATION</h3>
              <ContentWrap>
                <h4>2023.01.09 - 2023.02.28</h4>
                <Content> 프론트엔드 웹 개발자 양성 과정 - HTML5, CSS3, 자바스크립트(Java Script), 제이쿼리(JQuery), 리액트(React)</Content>
              </ContentWrap>
              <ContentWrap>
                <h4>2021.09.07 - 2022.01.25</h4>
                <Content> (디지털디자인)UI/UX 반응형 웹디자인 & 웹퍼블리셔B 수료</Content>
              </ContentWrap>
              <ContentWrap>
                <h4>2008.03.03 - 2014.08.14 </h4>
                <Content>부산외국어대학교 한국어문학부 졸업 /부전공: 일본어문학</Content>
              </ContentWrap>
            </Txt>
          </TextWrap>
        </Bg>
      </BgWrap>
    </Container>
  );
}

export default SectionAbout;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  width: 100%;
  height: 1000px;
  /* @media ${({ theme }) => theme.device.laptop} {
  }
  @media ${({ theme }) => theme.device.tablet} {
    height: 800px;
    transition: all 0.3s;
  } */
  @media ${({ theme }) => theme.device.mobile} {
    height: 800px;
    transition: all 0.3s;
    margin-bottom: 800px;
  }
`;

const Title = styled.div`
  z-index: 2;
  position: relative;
  width: 100%;
  max-width: 74%;
  margin: 0 auto;
  padding-top: 100px;
  @media ${({ theme }) => theme.device.laptop} {
    z-index: 2;
    position: relative;
    width: 100%;
    max-width: 80%;
    margin: 0 auto;
    padding-top: 100px;
  }
  @media ${({ theme }) => theme.device.tablet} {
    padding-top: 50px;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    padding-top: 100px;
    transition: all 0.3s;
    text-align: center;
  }

  h4 {
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 20px;
    @media ${({ theme }) => theme.device.laptop} {
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
    }
    @media ${({ theme }) => theme.device.tablet} {
      font-family: "Montserrat";
      font-weight: 500;

      font-size: 18px;
    }
    @media ${({ theme }) => theme.device.mobile} {
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
    }
  }

  h2 {
    width: 40%;
    font-weight: ${({ theme }) => theme.fonts.weightRegular};
    color: ${({ theme }) => theme.colors.black_color};
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;
    @media ${({ theme }) => theme.device.laptop} {
      width: 40%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
    @media ${({ theme }) => theme.device.tablet} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #fff;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
    @media ${({ theme }) => theme.device.mobile} {
      display: inline-block;
      width: 100%;
      font-family: "Montserrat";
      font-weight: 700;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
  }
  h3 {
    width: 30%;
    font-weight: ${({ theme }) => theme.fonts.weightRegular};
    color: ${({ theme }) => theme.colors.black_color};
    font-size: ${({ theme }) => theme.fonts.fontSemiMedium};
    line-height: 160%;
    margin-top: 40px;
    @media ${({ theme }) => theme.device.laptop} {
      width: 30%;
      color: ${({ theme }) => theme.colors.black_color};
      font-weight: 500;

      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-weight: 500;
      color: ${({ theme }) => theme.colors.black_color};
      font-size: 18px;
      line-height: 160%;
      margin-top: 30px;
    }
  }
`;

const Wrap = styled.div`
  z-index: 3;
  position: absolute;
  top: 100px;
  right: 0%;
  width: 100%;
  height: 100%;
  /* background: seagreen; */
  color: ${({ theme }) => theme.colors.white_color};
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 3;
    top: 400px;
    right: 0%;
    z-index: 10;
    width: 100%;
    height: 800px;
    /* background: seagreen; */
  }
`;

const Slide = styled.div`
  z-index: 3;
  position: absolute;
  top: 250px;
  right: 13%;
  width: 44.981608%;
  height: 585px;
  background: seagreen;
  @media ${({ theme }) => theme.device.laptop} {
    z-index: 3;
    position: absolute;
    top: 250px;
    right: 10%;
    width: 44.981608%;
    height: 450px;
    background: seagreen;
    transition: all 0.3s;
  }

  @media ${({ theme }) => theme.device.mobile} {
    z-index: 3;
    position: absolute;
    top: 500px;
    left: 10%;
    width: 80%;
    height: 400px;
    background: seagreen;
  }
`;

const SlideWrap = styled.ul`
  z-index: 2;
  position: absolute;
  bottom: 30px;
  left: -150px;

  @media ${({ theme }) => theme.device.mobile} {
    z-index: 3;
    position: relative;
    left: 0;
    top: 30px;
    text-align: center;
  }
`;

const SlideList = styled.li`
  display: inline-block;
  margin: 0 7px;
  a {
    z-index: 3;
    position: relative;
    font-weight: 700;
    color: #000;
    font-size: 90px;
    transition: all 0.4s;
    @media ${({ theme }) => theme.device.laptop} {
      position: relative;
      z-index: 2;
      font-weight: 700;
      color: #000;
      font-size: 3rem;
    }
    @media ${({ theme }) => theme.device.tablet} {
      position: relative;
      z-index: 2;
      font-weight: 700;
      color: #000;
      font-size: 3rem;
    }
    @media ${({ theme }) => theme.device.mobile} {
      position: relative;
      z-index: 2;
      font-weight: 700;
      color: #000;
      font-size: 3rem;
    }

    &::before {
      z-index: -1;
      content: "";
      position: absolute;
      left: 0%;
      top: 70%;
      width: 100%;
      height: 20px;
      box-shadow: inset 0px 0px 0px #0066ff;
      background: #5053582c;

      transition: all 0.4s cubic-bezier(0.5, 0.24, 0, 1);
    }

    &:hover {
      &::before {
        box-shadow: inset 650px 0px 0px #0066ff;
      }
    }
  }

  h4 {
    margin-top: 20px;
    font-weight: 500;
    color: #000;
    font-size: 26px;
  }
`;

const OneWrap = styled.div`
  display: none;
  z-index: 2;
  width: 100%;
  @media ${({ theme }) => theme.device.laptop} {
    display: flex;
    width: 50%;
    margin: 50px auto;
    justify-content: space-between;
  }
  @media ${({ theme }) => theme.device.mobile} {
    display: flex;
    width: 50%;
    margin: 50px auto;
    justify-content: space-between;
  }
`;

const OneList = styled.div`
  position: relative;
  margin-bottom: 15px;
`;

const One = styled.div`
  display: inline-block;

  width: 10px;
