import styled from "styled-components";
import { useState, useRef, useEffect } from "react";

function SectionAbout() {
  return (
    <Container id="about">
      <Title>
        <h2>about</h2>
      </Title>
      <Wrap></Wrap>
    </Container>
  );
}

export default SectionAbout;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 540px;
  background: #fff;
  margin-top: 130px;

  @media ${({ theme }) => theme.device.laptop} {
    display: flex;
    flex-direction: column;
  }
`;

const Wrap = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  @media ${({ theme }) => theme.device.laptop} {
  }
`;

const Title = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  @media ${({ theme }) => theme.device.laptop} {
    margin: 0 20px;
  }
  h2 {
    font-weight: 800;
    color: #000;
    font-size: 28px;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 26px;
    }
  }
`;
