import React, { useEffect } from "react";
import styled from "styled-components";

const RightNav = ({ open }) => {
  useEffect((open) => {
    const scroll = window.scrollY;
    document.body.style.cssText = `
      position: fixed;
      top: -${scroll}px;
      overflow: hidden;
      width: 100%;
      height: auto;
    `;
    return () => {
      document.body.style.cssText = "";
      window.scrollTo(0, scroll);
    };
  }, []);
  return (
    <Container open={open}>
      <Wrap>
        <Ul>
          <li>
            <Num>01</Num>
            <TxtWrap>
              <a href="./index.php" class="mainBtn_m">
                ABOUT
              </a>
              <span></span>
            </TxtWrap>
          </li>
          <li>
            <Num>02</Num>
            <TxtWrap>
              <a href="./sub/design_uiux.php" class="mainBtn_m">
                SKILLS
              </a>
              <span></span>
            </TxtWrap>
          </li>
          <li>
            <Num>03</Num>
            <TxtWrap>
              <a href="./sub/develop.php" class="mainBtn_m">
                DEVELOP
              </a>
              <span></span>
            </TxtWrap>
          </li>
          <li>
            <Num>04</Num>
            <TxtWrap>
              <a href="./sub/about.php" class="mainBtn_m">
                PROJECT
              </a>
              <span></span>
            </TxtWrap>
          </li>
        </Ul>
      </Wrap>
    </Container>
  );
};
const Container = styled.div`
  z-index: 50;
  display: block;
  position: fixed;
  top: 0;
  right: 0;
  background: #0c1017;
  width: 100%;
  height: 100%;
  transition: all 0.3s;
  transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
`;

const Wrap = styled.div`
  width: 100%;
  max-width: 1100px;
  margin: 0 auto;
`;
const Ul = styled.ul`
  /* z-index: 110;
  display: block;
  position: fixed;
  top: 0;
  right: 0;
  background: #0c1017;
  width: 100%;
  height: 100%;
  transition: all 0.3s;
  overflow: hidden;

  transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
  transition: transform 0.3s ease-in-out; */

  li {
    width: 50%;
    margin-top: 15%;
    float: left;
  }

  @media (max-width: 768px) {
    flex-flow: column nowrap;
    background-color: #0d2538;
    position: fixed;
    transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
    top: 0;
    right: 0;
    height: 100vh;
    width: 300px;
    padding-top: 3.5rem;
    transition: transform 0.3s ease-in-out;
    li {
      color: #fff;
    }
  }
`;

const Num = styled.div`
  display: inline-block;
  position: relative;
  bottom: 15px;
  font-weight: 500;
  font-size: 20px;
  color: #fff;
`;

const TxtWrap = styled.div`
  position: relative;
  display: inline-block;
  margin-left: 20px;

  a {
    z-index: 2;
    position: relative;
    display: inline-block;
    font-weight: 500;
    font-size: 60px;
    color: #fff;
  }
  span {
    z-index: 1;
    position: absolute;
    bottom: 0;
    left: 0;
    display: inline-block;
    width: 100%;
    background: #0066ff;
    height: 17px;
    transition: all 0.3s;
    transform: scale(0, 1);
  }
`;
export default RightNav;
