import styled, { keyframes } from "styled-components";

function SectionProject() {
  return (
    <Container>
      <SliderWrap>
        <Slider>
          <TextBanner>KEEP GOING</TextBanner>
          <TextBanner>KEEP GOING</TextBanner>
        </Slider>
        <Slider>
          <TextBanner>KEEP GOING</TextBanner>
          <TextBanner>KEEP GOING</TextBanner>
        </Slider>
      </SliderWrap>
    </Container>
  );
}

export default SectionProject;

const Container = styled.div`
  margin: 20px;
`;

const SliderWrap = styled.div`
  width: 25px;
  height: 420px;

  overflow: hidden;
  position: relative;
`;

const Slider = styled.div`
  display: block;
  width: 100%;
  height: 30px;

  position: absolute;
  overflow: hidden;
  ${(props) =>
    props.active &&
    `
  animation: ${TextAnimaition} 5s linear infinite;`}
`;

const TextBanner = styled.div`
  float: left;
  width: 50%;
`;

const TextAnimaition = keyframes`
     0% {
    left: 0;
    
  }
  100% {
    left:-100%
  }

`;
