import styled from "styled-components";
import SectionAbout from "./section/SectionAbout";
import SectionMain from "./section/SectionMain";
import SectionDevelop from "./section/SectionDevelop";
import Animation from "./section/Animation";
import SectionProject from "./section/SectionProject";
import SectionClose from "./section/SectionClose";

import SectionSkill from "./section/SectionSkill";
import { useEffect, useState } from "react";
import TopBtn from "./section/TopBtn";

function Main() {
  const TopBtn = () => {
    const [ScrollY, setScrollY] = useState(0);
    const [BtnStatus, setBtnStatus] = useState(false); // 버튼 상태

    const handleFollow = () => {
      setScrollY(window.pageYOffset);
      if (ScrollY > 100) {
        // 100 이상이면 버튼이 보이게
        setBtnStatus(true);
      } else {
        // 100 이하면 버튼이 사라지게
        setBtnStatus(false);
      }
    };

    const handleTop = () => {
      // 클릭하면 스크롤이 위로 올라가는 함수
      window.scrollTo({
        top: 0,
        behavior: "smooth",
      });
      setScrollY(0); // ScrollY 의 값을 초기화
      setBtnStatus(false); // BtnStatus의 값을 false로 바꿈 => 버튼 숨김
    };

    useEffect(() => {
      const watch = () => {
        window.addEventListener("scroll", handleFollow);
      };
      watch();
      return () => {
        window.removeEventListener("scroll", handleFollow);
      };
    });

    return <Container>{BtnStatus && <button className="topBtn">TOP</button>}</Container>;
  };

  return (
    <Container>
      <TopBtn />
      <SectionMain />
      <SectionSkill />
      <SectionAbout />
      <SectionDevelop />
      <Animation />
      <SectionProject />
      <SectionClose />
    </Container>
  );
}

export default Main;

const Container = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
`;
