import React, { Component } from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styled from "styled-components";

import { StyleSlider, Box } from "./StyleSlide";

export default class SimpleSlider extends Component {
  render() {
    const settings = {
      dots: true,
      arrow: true,
      infinite: true,
      autoplay: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      responsive: [
        {
          breakpoint: 1440,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
        {
          breakpoint: 720,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
        {
          breakpoint: 320,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
      ],
    };

    const Container = styled.div`
      z-index: 1;
      position: relative;
      top: 0;
      left: 0;
      width: 100%;
      height: 540px;
      background: #fff;
      margin-top: 130px;
    `;

    const Wrap = styled.div`
      position: relative;
      width: 100%;
      height: 100%;
    `;

    const Title = styled.div`
      width: 100%;
      max-width: 1400px;
      margin: 0 auto;
      @media ${({ theme }) => theme.device.laptop} {
        margin: 0 20px;
      }
      h2 {
        font-weight: 800;
        color: #000;
        font-size: 28px;
        @media ${({ theme }) => theme.device.laptop} {
          font-size: 26px;
        }
      }
    `;

    const SlideList = styled.div`
      width: 1400px;
      height: 500px;
      margin: 50px auto;
    `;

    return (
      <Container>
        <Wrap>
          <Title>
            <h2>Skills</h2>
          </Title>
          <SlideList>
            <StyleSlider {...settings}>
              <Box>
                <SlideTitle href="#main">html</SlideTitle>
              </Box>
              <Box>
                <SlideTitle href="#main">css</SlideTitle>
              </Box>
              <Box>
                <SlideTitle href="#main">javascript</SlideTitle>
              </Box>
              <Box>
                <SlideTitle href="#main">react</SlideTitle>
              </Box>
              <Box>
                <SlideTitle href="#main">photoshop</SlideTitle>
              </Box>
              <Box>
                <SlideTitle href="#main">xd</SlideTitle>
              </Box>
              <Box>
                <SlideTitle href="#main">figma</SlideTitle>
              </Box>
              <Box>
                <SlideTitle href="#main">github</SlideTitle>
              </Box>
            </StyleSlider>
          </SlideList>
        </Wrap>
      </Container>
    );
  }
}
