import { useState } from "react";
import styled from "styled-components";
import SideMenu from "./SideMenu";

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const showMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <Container>
      <Wrap>
        <Gnb>
          <Logo>ahreum</Logo>
          <div>
            <Nav>
              <ul>
                <li>
                  <a href="#about">ABOUT</a>
                </li>
                <li>
                  <a href="#about">SKILLS</a>
                </li>
                <li>
                  <a href="#develop">DEVELOP</a>
                </li>
                <li>
                  <a href="#project">PROJECT</a>
                </li>
              </ul>
            </Nav>
          </div>
        </Gnb>
      </Wrap>
      <SideWrap>
        <HamBtn open={showMenu} onClick={() => setMenuOpen(!menuOpen)}>
          <span></span>
          <span></span>
          <span></span>
        </HamBtn>
        <LineWrap open={showMenu} onClick={() => setMenuOpen(!menuOpen)}>
          <span></span>
          <span></span>
          <span></span>
        </LineWrap>
        {menuOpen && <SideMenu setMenuOpen={setMenuOpen}></SideMenu>}
      </SideWrap>
    </Container>
  );
}

export default Header;

const Container = styled.header`
  width: 100%;
  height: 100px;
  line-height: 100px;
  background-color: ${({ theme }) => theme.colors.main_color};
  color: ${({ theme }) => theme.fonts.font_color};
  font-size: ${({ theme }) => theme.fonts.fontMicro};
  font-weight: ${({ theme }) => theme.fonts.weightBold};
`;

const Wrap = styled.div`
  width: 100%;
  max-width: 1100px;
  margin: 0 auto;
`;
const Gnb = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled.h1`
  width: 10%;
  font-size: 1rem;
`;

const Nav = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  transform: ${({ menuOpen }) => (menuOpen ? "translateX(0)" : "translateX(-100%)")};
  ul {
    width: 60%;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    li {
      font-size: 15px;
      font-weight: ${({ theme }) => theme.colors.weightSemiBold};
      a {
        color: ${({ theme }) => theme.colors.white_color};
      }
    }
  }
`;

const SideWrap = styled.div`
  z-index: 5;
  display: block;
  position: fixed;
  top: 0;
  left: 50%;
  background: #0c1017;
  width: 100%;
  height: 100%;
  transition: all 0.3s;
  overflow: hidden;
`;

const HamBtn = styled.div`
  width: 60px;
  height: 45px;
  position: relative;
  margin: 50px auto;
  -webkit-transform: rotate(0deg);
  -moz-transform: rotate(0deg);
  -o-transform: rotate(0deg);
  transform: rotate(0deg);
  -webkit-transition: 0.5s ease-in-out;
  -moz-transition: 0.5s ease-in-out;
  -o-transition: 0.5s ease-in-out;
  transition: 0.5s ease-in-out;
  cursor: pointer;

  /* display: inline-block;
    span {
      &:first-child {
        overflow: hidden;
        position: relative;
        display: inline-block;
        width: 21px;
        height: 14px;
      }
      &:last-child {
        display: inline-block;
      } */
  span {
    display: block;
    position: absolute;
    height: 9px;
    width: 100%;
    background: #d3531a;
    border-radius: 9px;
    opacity: 1;
    left: 0;
    -webkit-transform: rotate(0deg);
    -moz-transform: rotate(0deg);
    -o-transform: rotate(0deg);
    transform: rotate(0deg);
    -webkit-transition: 0.25s ease-in-out;
    -moz-transition: 0.25s ease-in-out;
    -o-transition: 0.25s ease-in-out;
    transition: 0.25s ease-in-out;
  }

  &:first-child {
    top: ${({ menuOpen }) => (menuOpen ? "18px" : "0")};
    transform: ${({ menuOpen }) => (menuOpen ? "rotate(135deg)" : "rotate(0)")};
  }

  &:nth-child(2) {
    opacity: ${({ menuOpen }) => (menuOpen ? "0" : "1")};
    top: ${({ menuOpen }) => (menuOpen ? "0px" : "18px")};
    left: ${({ menuOpen }) => (menuOpen ? "-16px" : "0")};
  }

  &:last-child {
    top: ${({ menuOpen }) => (menuOpen ? "18px" : "36px")};
    transform: ${({ menuOpen }) => (menuOpen ? "rotate(-135deg)" : "rotate(0)")};
  }
`;

const LineWrap = styled.div`
  position: absolute;
  top: 5%;
  left: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 2rem;
  height: 2rem;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 10;

  &:focus {
    outline: none;
  }

  /* display: inline-block;
    span {
      &:first-child {
        overflow: hidden;
        position: relative;
        display: inline-block;
        width: 21px;
        height: 14px;
      }
      &:last-child {
        display: inline-block;
      } */
  i {
    display: block;
    position: absolute;
    height: 9px;
    width: 100%;
    background: #d3531a;
    border-radius: 9px;
    opacity: 1;
    left: 0;

    /* &:first-child {
          top: ${({ menuOpen }) => (menuOpen ? "50%" : "0")};
          margin-top: ${({ menuOpen }) => (menuOpen ? "-1px" : "0")};
          transform: ${({ menuOpen }) => (menuOpen ? "rotate(135deg)" : "rotate(0)")};
        } */
    &:first-child {
      top: ${({ menuOpen }) => (menuOpen ? "0px;" : "18px")};
      transform: ${({ menuOpen }) => (menuOpen ? "rotate(0deg)" : "rotate(135deg)")};
    }
    &:nth-child(1) {
      opacity: ${({ menuOpen }) => (menuOpen ? "1" : "0")};
      left: ${({ menuOpen }) => (menuOpen ? "0px" : "-60px")};
    }
    &:last-child {
      top: ${({ menuOpen }) => (menuOpen ? "36px;" : "-18px")};
      transform: ${({ menuOpen }) => (menuOpen ? "rotate(0deg)" : "rotate(-135deg)")};
    }
    /* &:last-child {
          bottom: ${({ menuOpen }) => (menuOpen ? "50%" : "0")};
          margin-bottom: ${({ menuOpen }) => (menuOpen ? "-1px" : "0")};
          transform: ${({ menuOpen }) => (menuOpen ? "rotate(-135deg)" : "rotate(0)")};
        } */
  }
`;
