import styled from "styled-components";
import SectionAbout from "./section/SectionAbout";
import SectionMain from "./section/SectionMain";

import SectionDevelop from "./section/SectionDevelop";
import Animation from "./section/Animation";
import SectionProject from "./section/SectionProject";
import SectionClose from "./section/SectionClose";

function Main() {
  let calcFont = 0;
  let fontSize = 0;
  let w = $(window);
  function setFontSize(w) {
    if (w.width() < 480) {
      calcFont = w.width() / 480;
      fontSize = 20 * calcFont;
      $("html").css({ "font-size": fontSize + "px" });
    } else {
      $("html").css({ "font-size": "20px" });
    }
  }
  setFontSize($(window));
  $(window).resize(function () {
    setFontSize($(this));
  });
  return (
    <Container>
      <SectionMain />
      <SectionAbout />

      <SectionDevelop />
      <Animation />
      <SectionProject />
      <SectionClose />
    </Container>
  );
}

export default Main;

const Container = styled.div`
  width: 100%;
  height: 100%;
`;
