      font-weight: 500;

      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }
    @media ${({ theme }) => theme.device.tablet} {
      width: 70%;
      font-weight: 500;

      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }
    @media ${({ theme }) => theme.device.mobile} {
      display: inline-block;
      width: 100%;
      font-weight: 500;
      color: ${({ theme }) => theme.colors.black_color};
      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }
  }
`;

