const OneList = styled.div`
  position: relative;
  margin-bottom: 15px;
`;

const One = styled.div`
  display: inline-block;

  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: #3f72af;

  margin-bottom: 1px;
`;

const BtnWrap = styled.ul`
  z-index: 2;
  position: absolute;
  top: 20%;
  left: 30%;
  @media ${({ theme }) => theme.device.laptop} {
    left: 20%;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 2;
    position: absolute;
    top: 150px;
    left: 0;
    text-align: center;
    width: 100%;
  }
`;

const BtnList = styled.li`
  position: relative;
  margin-bottom: 15px;
  @media ${({ theme }) => theme.device.mobile} {
    position: relative;
    display: inline-block;
    margin: 0 2px;
  }
  &:hover {
    color: #000;
    &::after {
      content: "";
      display: block;
      width: 34px;
      height: 1px;
      background: #8e8f9a;

      position: absolute;

      top: 50%;
      left: -15%;

      transition: all 0.3s ease;
    }
  }
  div {
    display: inline-block;
  }
  a {
    z-index: 3;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 80px;
    }
  }
  div {
    width: 34px;
    height: 1px;
    background: transparent;
    margin-bottom: 6px;
    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      display: none;
    }
  }
`;

const BtnTxt = styled.div`
  font-weight: 500;
  color: #8e8f9a;
  font-size: 18px;
  &:hover {
    color: #000;
  }
`;
const Txt = styled.div`
  z-index: 5;
  position: absolute;
  top: 0;
  left: 10%;
  width: 80%;
  color: ${({ theme }) => theme.colors.white_color};

  @media ${({ theme }) => theme.device.mobile} {
    display: none;
  }
  h3 {
    text-align: left;
    font-weight: 500;
    font-size: 2rem;
    padding-top: 50px;
    &::after {
      content: "";
      display: block;
      width: 100%;
      height: 2px;
      background: ${({ theme }) => theme.colors.white_color};
      margin: 10px auto;
    }
    @media ${({ theme }) => theme.device.laptop} {
      width: 100%;
    }
    @media ${({ theme }) => theme.device.tablet} {
      width: 100%;
    }
  }
`;

const ContentWrap = styled.div`
  position: relative;
  display: flex;
  justify-content: flex-start;
  width: 100%;
  line-height: 160%;

  h4 {
    width: 40%;
    font-size: 1rem;
    color: ${({ theme }) => theme.colors.white_color};
  }
`;

const Content = styled.div`
  /* position: absolute;
  top: 10%;
  left: 30%; */
  width: 50%;
  font-size: 1rem;
  color: ${({ theme }) => theme.colors.white_color};
`;
