import styled, { keyframes } from "styled-components";

function Animation() {
  return (
    <Container>
      <TextBanner></TextBanner>
    </Container>
  );
}

export default Animation;

const boxAnimation = keyframes`
  0% {
    left: 0;
    background: red;
  }
 
  100% {
    left: 100%;
    background: purple;
  }
`;
const Container = styled.div`
  width: 100%;
  max-width: 1400px;
  height: 300px;
  position: relative;
`;
const TextBanner = styled.div`
  width: 100%;
  height: 100px;
  background: red;
  border-radius: 0%;
  position: absolute;
  top: 20px;
  left: 20px;
  overflow: hidden;
  animation: ${boxAnimation} 3s 0s infinite linear alternate;
`;
