import { useState } from "react";
import styled from "styled-components";
import SideMenu from "./SideMenu";

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const showMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const [change, setChange] = useState(false);

  const animate = () => {
    // Button begins to shake
    setChange(true);

    // Buttons stops to shake after 2 seconds
    setTimeout(() => setChange(false), 2000);
  };

  return (
    <Container>
      <Wrap>
        <Gnb>
          <Logo>ahreum</Logo>
          <Nav>
            <ul>
              <li>
                <a href="#about">ABOUT</a>
              </li>
              <li>
                <a href="#about">SKILLS</a>
              </li>
              <li>
                <a href="#develop">DEVELOP</a>
              </li>
              <li>
                <a href="#project">PROJECT</a>
              </li>
            </ul>
          </Nav>
          <HamBtn
            onClick={() => {
              showMenu();
            }}
          >
            <a href="#">
              <span>
                <i></i>
                <i></i>
                <i></i>
              </span>
            </a>
          </HamBtn>
        </Gnb>
      </Wrap>
      {menuOpen && <SideMenu setMenuOpen={setMenuOpen}></SideMenu>}
    </Container>
  );
}

export default Header;

const Container = styled.header`
  width: 100%;
  height: 100px;
  line-height: 100px;
  background-color: ${({ theme }) => theme.colors.main_color};
  color: ${({ theme }) => theme.fonts.font_color};
  font-size: ${({ theme }) => theme.fonts.fontMicro};
  font-weight: ${({ theme }) => theme.fonts.weightBold};
`;

const Wrap = styled.div`
  width: 100%;
  max-width: 1100px;
  margin: 0 auto;
`;
const Gnb = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled.h1`
  width: 10%;
  font-size: 1rem;
`;

const Nav = styled.div`
  width: 80%;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  ul {
    width: 60%;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    li {
      font-size: 15px;
      font-weight: ${({ theme }) => theme.colors.weightSemiBold};
      a {
        color: ${({ theme }) => theme.colors.white_color};
      }
    }
  }
`;

const HamBtn = styled.div`
  z-index: 2;
  position: relative;
  float: right;
  top: 60px;
  transition: all 0.3s;
  a {
    display: inline-block;
    span {
      &:first-child {
        overflow: hidden;
        position: relative;
        display: inline-block;
        width: 21px;
        height: 14px;
      }
      &:last-child {
        display: inline-block;
      }
      i {
        position: absolute;
        left: 0;
        display: block;
        width: 100%;
        height: 1px;
        background: ${({ theme }) => theme.colors.white_color};
        transition: all 0.3s;
        &:nth-child(0) {
          top: 0;
        }
        &:nth-child(1) {
          width: 70%;
          top: 50%;
          margin-top: -1px;
        }
        &:nth-child(2) {
          bottom: 0;
        }
      }
    }
  }
`;
