import styled from "styled-components";

function SectionAbout() {
  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <ViewBtn>
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </ViewBtn>
      </Title>

      <BgWrap>
        <Bg></Bg>
      </BgWrap>
    </Container>
  );
}

export default SectionAbout;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 1000px;
  background: #fff;

  @media ${({ theme }) => theme.device.mobile} {
    height: 1000px;
    margin-top: 80px;
  }
`;

const Title = styled.div`
  z-index: 2;
  position: relative;
  width: 100%;
  max-width: 74%;
  margin: 0 auto;
  padding-top: 100px;

  @media ${({ theme }) => theme.device.laptop} {
    z-index: 2;
    position: relative;
    width: 100%;
    max-width: 80%;
    margin: 0 auto;
    padding-top: 100px;
  }
  @media ${({ theme }) => theme.device.tablet} {
    padding-top: 50px;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    padding-top: 100px;
    transition: all 0.3s;
    text-align: center;
  }
  h4 {
    font-weight: 500;
    color: #000;
    font-size: 20px;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 18px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      color: #050505;
      font-size: 18px;
    }
  }
  h2 {
    width: 40%;
    font-family: "Montserrat";
    font-weight: 600;
    color: #000;
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;
    @media ${({ theme }) => theme.device.laptop} {
      width: 40%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #050505;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
  }
  h3 {
    width: 30%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 18px;
    line-height: 160%;
    margin-top: 40px;
    @media ${({ theme }) => theme.device.laptop} {
      width: 30%;
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 400;
      color: #050505;
      font-size: 18px;
      line-height: 160%;
      margin-top: 30px;
    }
  }
`;

const Wrap = styled.div`
  z-index: 3;
  position: absolute;
  top: 250px;
  right: 13%;
  width: 45%;
  height: 585px;
  background: #1b262c;
  @media ${({ theme }) => theme.device.laptop} {
    z-index: 3;
    position: absolute;
    top: 250px;
    right: 10%;
    width: 45%;
    height: 450px;

    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      z-index: 3;
      position: absolute;
      top: 500px;
      left: 10%;
      width: 80%;
      height: 400px;
    }
  }
`;

const SlideView = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
`;

const ProfileWrap = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const Left = styled.div`
  position: absolute;
  width: 50%;
  height: 100%;
  top: 0;
  left: 0;
  padding: 5%;
  font-size: 2rem;
  color: #d8d8d8;
  word-break: break-all;

  div {
    h3 {
      font-weight: 500;
      color: #fff;
      font-size: 1.65rem;
      margin: 5% auto;
      text-align: left;

      &::after {
        content: "";
        display: block;
        width: 100%;
        height: 2px;
        background: #f0f0f0;
        margin-top: 10px;
      }
    }
  }
`;

const Right = styled.div`
  position: absolute;
  width: 50%;
  height: 100%;
  /* background: #245953; */
  right: 0;
  padding: 5%;

  h3 {
    font-weight: 500;
    color: #fff;
    font-size: 1.65rem;
    margin: 5% auto;
    text-align: left;

    &::after {
      content: "";
      display: block;
      width: 100%;
      height: 2px;
      background: #f0f0f0;
      margin-top: 10px;
    }
  }
`;

const Content = styled.div`
  font-size: 1rem;
  color: #f5f5f5;

  div {
    display: flex;
    /* flex-direction: column; */
    /* justify-content: space-between; */

    padding: 5%;
    h4 {
      width: 40%;
      margin-right: 5%;
      color: #fff;
    }
  }
`;

const Date = styled.h4`
  font-size: 1rem;
  color: #fff;
  margin: 3% auto;
`;

const ViewBtn = styled.div`
  z-index: 3;
  position: absolute;
  bottom: -50px;
  left: 0;
  display: inline-block;
  width: auto;
  height: 25px;
  /* background: red; */
  margin-top: 40px;
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 3;
    position: relative;
    display: inline-block;
    height: 25px;
    /* background: red; */
    margin-top: 0;
  }

  a {
    z-index: 2;
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 15px;
    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      text-align: center;
      z-index: 2;
      position: relative;
      display: inline-block;
      width: 100%;
      height: 100%;
      font-family: "Montserrat";
      font-weight: 500;
      color: #fff;
      font-size: 15px;
      transition: all 0.3s;
    }
  }
`;

const BtnWrap = styled.ul`
  z-index: 2;
  position: absolute;
  bottom: 30px;
  left: -150px;

  @media ${({ theme }) => theme.device.mobile} {
    z-index: 3;
    position: relative;
    left: 0;
    top: 30px;
    text-align: center;
  }
  li {
    display: inline-block;
    margin: 0 7px;
    a {
      font-family: "Montserrat";
      font-weight: 500;
      color: #b7b7b7;
      font-size: 15px;
    }
  }
`;
const BtnList = styled.li`
  position: relative;
  margin-bottom: 15px;
  @media ${({ theme }) => theme.device.mobile} {
    position: relative;
    display: inline-block;
    margin: 0 2px;
  }

  div {
    display: inline-block;
  }
  a {
    z-index: 3;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 80px;
    }
  }
`;

const BtnTxt = styled.div`
  font-weight: 500;
  color: #8e8f9a;
  font-size: 18px;
  &:hover {
    color: #000;
  }
`;

const BgWrap = styled.ul`
  z-index: 1;
  position: absolute;
  top: 0;
  right: 0;
  width: 50%;
  height: 100%;
  @media ${({ theme }) => theme.device.laptop} {
    z-index: 1;
    position: absolute;
    top: 0;
    right: 0;
    width: 45%;
    height: 100%;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    width: 100%;
  }
`;

const Bg = styled.li`
  z-index: 10;
  position: absolute;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  background: #f9f7f7;
  h4 {
    font-weight: 500;
    color: #fff;
    font-size: 20px;
    font-family: "Montserrat";
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 18px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      color: #050505;
      font-size: 18px;
    }
  }
  h2 {
    width: 40%;
    font-family: "Montserrat";
    font-weight: 600;
    color: #000;
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;
    @media ${({ theme }) => theme.device.laptop} {
      width: 40%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #050505;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
  }
  h3 {
    width: 30%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 18px;
    line-height: 160%;
    margin-top: 40px;
    @media ${({ theme }) => theme.device.laptop} {
      width: 30%;
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 400;
      color: #050505;
      font-size: 18px;
      line-height: 160%;
      margin-top: 30px;
    }
  }
`;
