import styled from "styled-components";
import { useState, useEffect, useRef } from "react";

function SectionAbout() {
  const data = [
    {
      id: 0,
      title: "sociable",
      content: "sociable",
      contents: "사교적인",
      dots: "",
    },
    {
      id: 1,
      title: "enterprising",
      content: "enterprising",
      contents: "진취력있는",
      dots: "",
    },
    {
      id: 2,
      title: "shrewd",
      content: "shrewd",
      contents: "catch on fast",
      dots: "",
    },
    {
      id: 3,
      title: "sociable",
      content: "sociable",
      contents: "사교적인",
      dots: "",
    },
    {
      id: 4,
      title: "considerate",
      content: "considerate",
      contents: "배려하는",
      dots: "",
    },
    {
      id: 5,
      title: "hardworking",
      content: "hardworking",
      contents: "성실한",
      dots: "",
    },
    {
      id: 6,
      title: "figma",
      content: "figma",
      contents: "김아름",
      dots: "",
    },
    {
      id: 7,
      title: "responsibility",
      content: "responsibility",
      contents: "책임감",
      dots: "",
    },
  ];

  const delay = 2500;
  const [index, setIndex] = useState(0);

  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

    return () => {
      resetTimeout();
    };
  });
  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>skills</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
      </Title>
      <Wrap>
        <BtnWrap></BtnWrap>
      </Wrap>
      <BgWrap>
        <Bg>
          <Slide>
            <SlideWrap>
              {data
                .filter((item) => index === item.id)
                .map((item) => (
                  <SlideList>
                    <SlideTxt>
                      <a href="#main">{item.content}</a>
                      <span></span>
                      <h4>{item.contents}</h4>
                    </SlideTxt>
                    <OneWrap>
                      {data.map((item) => (
                        <OneList
                          key={item.id}
                          className={index === item.id ? SlideWrap.active : null}
                          onClick={() => setIndex(item.id)}
                          style={{ transform: `translate3d(${index}%)`, transition: "all 3s" }}
                        >
                          <One key={item.id} className={index === item.id ? SlideWrap.active : null} style={index === item.id ? { background: "#000" } : { background: "#8e8f9a" }}>
                            {item.dots}
                          </One>
                          {data.map((index) => (
                            <div key={index}></div>
                          ))}
                        </OneList>
                      ))}
                    </OneWrap>
                  </SlideList>
                ))}
            </SlideWrap>
          </Slide>
        </Bg>
      </BgWrap>
    </Container>
  );
}

