import styled, { keyframes } from "styled-components";
import { useState, useRef, useEffect } from "react";

const SectionSkill = () => {
  const data = [
    {
      id: 0,
      title: "html",
      content: "html",
      contents: "1. html ",
    },
    {
      id: 1,
      title: "css",
      content: "css",
      contents: "2.css ",
    },
    {
      id: 2,
      title: "JavaScript",
      content: "JavaScript",
      contents: "3.JavaScript ",
    },
    {
      id: 3,
      title: "react",
      content: "react",
      contents: "4. react",
    },
    {
      id: 4,
      title: "photoshop",
      content: "photoshop",
      contents: "5.photoshop ",
    },
    {
      id: 5,
      title: "xd",
      content: "xd",
      contents: "6.xd ",
    },
    {
      id: 6,
      title: "figma",
      content: "figma",
      contents: "7.figma ",
    },
    {
      id: 7,
      title: "github",
      content: "github",
      contents: "8. github",
    },
  ];

  const delay = 2500;
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

    return () => {
      resetTimeout();
    };
  });
  return (
    <Container>
      <Wrap>
        <Title>
          <h2>about</h2>
        </Title>
        <Slide>
          <SlideWrap>
            {data
              .filter((item) => index === item.id)
              .map((item) => (
                <SlideList>
                  <SlideTxt>
                    <a href="#main">{item.content}</a>
                    <span></span>
                    <div></div>
                    <h4>a revelatory insight</h4>
                  </SlideTxt>
                </SlideList>
              ))}
          </SlideWrap>
        </Slide>
        <BtnWrap>
          {data.map((item) => (
            <BtnList key={item.id} className={index === item.id ? SlideWrap.active : null} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)` }}>
              <BtnTxt>{item.title}</BtnTxt>
              {data.map((index) => (
                <div key={index}></div>
              ))}
            </BtnList>
          ))}
        </BtnWrap>
        <BgWrap>
          <Bg></Bg>
          <Txt>
            <p>words that express me</p>
          </Txt>
        </BgWrap>
      </Wrap>
    </Container>
  );
};
export default SectionSkill;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 540px;
  background: #fff;
  margin-top: 130px;
  @media ${({ theme }) => theme.device.mobile} {
    height: 400px;
    margin-top: 80px;
  }
`;

const Wrap = styled.div`
  position: relative;
  width: 100%;
  height: 100%;

  @media ${({ theme }) => theme.device.laptop} {
    position: relative;
    width: 100%;
    height: 100%;
    margin-top: 70px;
  }
`;

const Title = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  @media ${({ theme }) => theme.device.laptop} {
    margin: 0 20px;
  }
  @media ${({ theme }) => theme.device.tablet} {
    margin: 0 40px;
  }
  h2 {
    font-weight: 800;
    color: #000;
    font-size: 28px;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 26px;
    }
  }
`;

const Slide = styled.div`
  z-index: 1;
  position: relative;
  width: 100%;
  height: 969px;
`;

const SlideWrap = styled.ul`
  position: relative;
  width: 100%;
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 2;
    position: absolute;
    top: 150px;
    left: 0;
    text-align: center;
    width: 100%;
  }
`;

const SlideList = styled.li`
  position: absolute;
  top: 0;
  left: 50%;
  width: 50%;
  height: 300px;
  background: #fff;
  @media ${({ theme }) => theme.device.laptop} {
    z-index: 1;
    position: absolute;
    top: 0;
    left: 40%;
    width: 60%;
    height: 300px;
    background: #fff;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 1;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 300px;
    /* background: rosybrown; */
    text-align: center;
  }
`;

const SlideTxt = styled.div`
  position: relative;
  display: inline-block;

  a {
    z-index: 2;
    position: relative;

    font-weight: 700;
    color: #000;
    font-size: 100px;
    transition: all 0.4s;
    @media ${({ theme }) => theme.device.mobile} {
      display: inline-block;
      z-index: 2;
      position: relative;

      font-weight: 700;
      color: #000;
      font-size: 70px;
    }

    &::before {
      z-index: -1;
      content: "";
      position: absolute;
      left: 0%;
      top: 70%;
      width: 100%;
      height: 20px;
      box-shadow: inset 0px 0px 0px #0066ff;
      background: #5053582c;

      transition: all 0.4s cubic-bezier(0.5, 0.24, 0, 1);
    }
    &:hover {
      &::before {
        box-shadow: inset 600px 0px 0px #0066ff;
      }
    }
  }
  span {
    z-index: 1;
    /* bottom: 0;
    left: 100%;
    width: 100%;
    height: 20px; */

    transition: all 0.7s;
    position: absolute;

    /* z-index: 1;
    display: inline-block;
    position: absolute;
    bottom: 50px;
    left: 0;
    width: 100%;
    height: 20px;
    background: #5053582c;
    transition: all 0.7s;
    overflow: hidden; */

    &:hover {
      &::before {
        box-shadow: inset 600px 0px 0px #0066ff;
      }
    }
  }
  div {
    display: none;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: #8e8f9a;
    margin-bottom: 1px;
    @media ${({ theme }) => theme.device.mobile} {
      display: inline-block;
      position: absolute;
    }
  }

  h4 {
    margin-top: 20px;
    font-weight: 500;
    color: #000;
    font-size: 26px;
  }
`;

const BtnWrap = styled.ul`
  z-index: 2;
  position: absolute;
  top: 20%;
  left: 30%;
  @media ${({ theme }) => theme.device.laptop} {
    left: 20%;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 2;
    position: absolute;
    top: 150px;
    left: 0;
    text-align: center;
    width: 100%;
  }
`;

const BtnList = styled.li`
  position: relative;
  margin-bottom: 15px;
  @media ${({ theme }) => theme.device.mobile} {
    position: relative;
    display: inline-block;
    margin: 0 2px;
  }
  &:hover {
    color: #000;
    &::after {
      content: "";
      display: block;
      width: 34px;
      height: 1px;
      background: #8e8f9a;

      position: absolute;

      top: 50%;
      left: -15%;

      transition: all 0.3s ease;
    }
  }
  div {
    display: inline-block;
  }
  a {
    z-index: 3;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 80px;
    }
  }
  div {
    width: 34px;
    height: 1px;
    background: transparent;
    margin-bottom: 6px;
    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      display: none;
    }
  }
`;

const BtnTxt = styled.div`
  font-weight: 500;
  color: #8e8f9a;
  font-size: 18px;
  &:hover {
    color: #000;
  }
`;

const BgWrap = styled.div`
  position: absolute;
  top: 250px;
  right: 0;
  width: 50%;
  height: 100%;
  @media ${({ theme }) => theme.device.laptop} {
