import styled from "styled-components";
import { useState, useRef, useEffect } from "react";

function SectionAbout() {
  return (
    <Container>
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <div class="s3ViewBtn_wrap">
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </div>
      </Title>
      <Wrap>
        <div class="s3Slide_view">
          <ul class="s3Slide_wrap">
            <li class="s3Slide s3Slide0" style="z-index: 3; opacity: 0;">
              <div></div>
            </li>
            <li class="s3Slide s3Slide1" style="z-index: 1; opacity: 0;">
              <div></div>
            </li>
            <li class="s3Slide s3Slide2" style="z-index: 2; opacity: 1;">
              <div></div>
            </li>
          </ul>
        </div>

        <ul class="s3PageBtn_wrap">
          <li class="adds3Page">
            <a href="javascript:" class="s3PageBtn">
              01
            </a>
          </li>
          <li class="">
            <a href="javascript:" class="s3PageBtn">
              02
            </a>
          </li>
          <li class="">
            <a href="javascript:" class="s3PageBtn">
              03
            </a>
          </li>
        </ul>
      </Wrap>
      <div class="bg_wrap">
        <div class="bg"></div>
      </div>
    </Container>
  );
}

export default SectionAbout;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 540px;
  background: #fff;
  margin-top: 130px;

  @media ${({ theme }) => theme.device.laptop} {
    display: flex;
    flex-direction: column;
  }
`;

const Wrap = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  @media ${({ theme }) => theme.device.laptop} {
  }
`;

const Title = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  @media ${({ theme }) => theme.device.laptop} {
    margin: 0 20px;
  }
  h2 {
    font-weight: 800;
    color: #000;
    font-size: 28px;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 26px;
    }
  }
`;
