import styled, { keyframes } from "styled-components";

function Animation() {
  return (
    <Container>
      <TextBanner>
        <MainColor>KEEP GOING KEEP GOING</MainColor>
        <WhiteColor>KEEP GOING</WhiteColor>
      </TextBanner>
    </Container>
  );
}

export default Animation;

const boxAnimation = keyframes`
  0% {
    left: 0;

  }
 
  100% {
    left: -100%;
 
  }
`;
const Container = styled.div`
  width: 100%;
  overflow: hidden;
  height: 300px;
  position: relative;
`;
const TextBanner = styled.div`
  display: block;
  width: 200%;
  height: 100px;
  font-size: ${({ theme }) => theme.fonts.fontExtra};
  font-weight: ${({ theme }) => theme.fonts.weightExtra};
  border-radius: 0%;
  overflow: hidden;
  position: absolute;
  top: 20px;
  left: 20px;
  animation: ${boxAnimation} 8s infinite linear;
`;

const MainColor = styled.span`
  float: left;
  width: 50%;
  color: ${({ theme }) => theme.colors.main_color};
`;

const WhiteColor = styled.span`
  color: ${({ theme }) => theme.colors.White_color};
`;
