import React, { Component } from "react";
import styled from "styled-components";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default class SimpleSlider extends Component {
  render() {
    const settings = {
      dots: true,
      arrow: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
    };

    const Container = styled.div`
      z-index: 1;
      position: relative;
      top: 0;
      left: 0;
      width: 100%;
      height: 540px;
      background: #fff;
      margin-top: 130px;
    `;

    const Wrap = styled.div`
      position: relative;
      width: 100%;
      height: 100%;
    `;

    const Title = styled.div`
      width: 100%;
      max-width: 1400px;
      margin: 0 auto;
      @media ${({ theme }) => theme.device.laptop} {
        margin: 0 20px;
      }
      h2 {
        font-weight: 800;
        color: #000;
        font-size: 28px;
        @media ${({ theme }) => theme.device.laptop} {
          font-size: 26px;
        }
      }
    `;

    const SlideList = styled.div`
      width: 1400px;
      margin: 0 auto;
    `;

    return (
      <Container>
        <Wrap>
          <Title>
            <h2>Skills</h2>
          </Title>
          <SlideList>
            <Slider {...settings}>
              <div>
                <h3>html</h3>
              </div>
              <div>
                <h3>css</h3>
              </div>
              <div>
                <h3>JavaScript</h3>
              </div>
              <div>
                <h3>react</h3>
              </div>
              <div>
                <h3>photoshop</h3>
              </div>
              <div>
                <h3>xd</h3>
              </div>
              <div>
                <h3>figma</h3>
              </div>
              <div>
                <h3>github</h3>
              </div>
            </Slider>
          </SlideList>
        </Wrap>
      </Container>
    );
  }
}
