import styled from "styled-components";
import { useState } from "react";

function SectionAbout() {
  const data = [
    {
      id: 0,
      title: "1",
      content: "sociable",
      contents: "사교적인",
      dots: "",
    },
    {
      id: 1,
      title: "2",
      content: "enterprising",
      contents: "진취력있는",
      dots: "",
    },
    {
      id: 2,
      title: "3",
      content: "shrewd",
      contents: "catch on fast",
      dots: "",
    },
  ];

  const [index, setIndex] = useState(0);

  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <ViewBtn>
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </ViewBtn>
      </Title>
      <Wrap>
        <SlideView>
          <ProfileWrap>
            <Left>
              <div class="top">
                <h2>PROFILE</h2>
                {/* <img src="/img/kim_img.jpg" /> */}
              </div>
              <div class="bottom">
                <p>Name 김아름</p>
                <p>서울 마포구 거주</p>
                <p>1989.03.07 </p>
                <p>010.2967.5037</p>
                <p>dkfma0307@naver.com</p>
              </div>
            </Left>
            <Right>
              <div>
                <p class="tit">EDUCATION</p>
                <div class="content">
                  <p>2021.09.07 - 2022.01.25 (디지털디자인)UI/UX 반응형 웹디자인&amp;웹퍼블리셔B 수료</p>

                  <p>2008.03 - 2014. 10 부산외국어대학교 한국어문학부 졸업 부전공: 일본어문학</p>
