import styled from "styled-components";

function SectionClose() {
  return (
    <Container>
      <Wrap>
        <img className="cilcle" alt="circle_bg" src="img/Ellipse5.png" />
        <TextBox>
          <p>Thank you for taking your time for my portfolio. </p>
        </TextBox>
      </Wrap>
    </Container>
  );
}

export default SectionClose;

const Container = styled.div`
  width: 100%;
  height: 40vw;

  margin: 0 auto;

  background-color: ${({ theme }) => theme.colors.gray_color};

  position: relative;
`;

const Wrap = styled.div`
  width: 60%;
  height: 100%;
  margin: 10% auto;

  display: flex;
  text-align: center;
  justify-content: center;
  align-items: center;
`;

const TextBox = styled.div`
  p {
    font-size: ${({ theme }) => theme.fonts.fontExtra};
    font-weight: ${({ theme }) => theme.fonts.weightSemiRegular};
  }
`;
