import styled from "styled-components";
import { useState } from "react";

function SectionAbout() {
  const data = [
    {
      id: 0,
      title: "1",
      content: "sociable",
      contents: "사교적인",
      dots: "",
    },
    {
      id: 1,
      title: "2",
      content: "enterprising",
      contents: "진취력있는",
      dots: "",
    },
    {
      id: 2,
      title: "3",
      content: "shrewd",
      contents: "catch on fast",
      dots: "",
    },
  ];

  const [index, setIndex] = useState(0);

  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <ViewBtn>
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </ViewBtn>
      </Title>
      <Wrap>
        <SlideView>
          {data
            .filter((item) => index === item.id)
            .map((item) => (
              <SlideWrap key={item.id} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)` }}>
                <li>
                  <ProfileWrap>
                    <Left>
                      <h2>PROFILE</h2>
                    </Left>
                    <Right>
                      <ul>
                        <li>
                          <h4>name</h4>
                          <p>
                            Kim Ah Reum 김아름
                            <span></span>
                          </p>
                        </li>
                        <li>
                          <h4>address</h4>
                          <p>
                            서울특별시 마포구 성산동
                            <span></span>
                          </p>
                        </li>
                        <li>
                          <h4>birth</h4>
                          <p>
                            1989.03.07
                            <span></span>
                          </p>
                        </li>
                        <li>
                          <h4>phone number</h4>
                          <p>010.2967.5037</p>
                        </li>
                        <li>
                          <h4>e-mail</h4>
                          <p>
                            aarreum0307@gmail.com
                            <span></span>
                          </p>
                        </li>
                      </ul>
                    </Right>
                  </ProfileWrap>
                </li>
                <li>
                  <Education>
                    <h2>부산외국어대학교</h2>
                  </Education>
                </li>
                <li>
                  <div>
                    <h2>경력</h2>
                  </div>
                </li>
              </SlideWrap>
            ))}
        </SlideView>
        <BtnWrap>
          {data.map((item) => (
            <BtnList key={item.id} className={index === item.id ? SlideWrap.active : null} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)` }}>
              <BtnTxt>{item.title}</BtnTxt>
              {data.map((index) => (
                <div key={index}></div>
              ))}
            </BtnList>
          ))}
        </BtnWrap>
      </Wrap>
      <BgWrap>
        <Bg></Bg>
      </BgWrap>
    </Container>
  );
}

export default SectionAbout;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 1000px;
  background: #fff;

  @media ${({ theme }) => theme.device.mobile} {
    height: 1000px;
    margin-top: 80px;
  }
`;

const Title = styled.div`
  z-index: 2;
  position: relative;
  width: 100%;
  max-width: 74%;
  margin: 0 auto;
  padding-top: 100px;

  @media ${({ theme }) => theme.device.laptop} {
    z-index: 2;
    position: relative;
    width: 100%;
    max-width: 80%;
    margin: 0 auto;
    padding-top: 100px;
  }
  @media ${({ theme }) => theme.device.tablet} {
    padding-top: 50px;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    padding-top: 100px;
    transition: all 0.3s;
    text-align: center;
  }
  h4 {
    font-weight: 500;
    color: #000;
    font-size: 20px;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 18px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      color: #050505;
      font-size: 18px;
    }
  }
  h2 {
    width: 40%;
    font-family: "Montserrat";
    font-weight: 600;
    color: #000;
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;
    @media ${({ theme }) => theme.device.laptop} {
      width: 40%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #050505;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
  }
  h3 {
    width: 30%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 18px;
    line-height: 160%;
    margin-top: 40px;
    @media ${({ theme }) => theme.device.laptop} {
      width: 30%;
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 400;
      color: #050505;
      font-size: 18px;
      line-height: 160%;
      margin-top: 30px;
    }
  }
`;

const Wrap = styled.div`
  z-index: 3;
  position: absolute;
  top: 250px;
  right: 13%;
  width: 45%;
  height: 585px;
  background: #222831;
  @media ${({ theme }) => theme.device.laptop} {
    z-index: 3;
    position: absolute;
    top: 250px;
    right: 10%;
    width: 45%;
    height: 450px;

    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      z-index: 3;
      position: absolute;
      top: 500px;
      left: 10%;
      width: 80%;
      height: 400px;
    }
  }
`;

const SlideView = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
`;

const SlideWrap = styled.ul`
  position: relative;
  width: 100%;
  height: 100%;
`;

const ProfileWrap = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;

const Education = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;

const Left = styled.div`
  position: absolute;
  width: 50%;
  height: 100%;
  left: 0;
  font-size: 6rem;
  color: #fff;
  word-break: break-all;
  h2 {
    color: #fff;
    width: 80%;
    height: 50%;
    line-height: 100%;
    margin: 30% auto;
  }
`;

const Right = styled.div`
  position: absolute;
  width: 50%;
  height: 100%;
  background: #393e46;
  right: 0;
  ul {
    width: 90%;
    height: 66%;
    margin: 100px auto;

    text-align: left;

    li {
      margin-bottom: 20px;

      h4 {
        font-weight: 500;
        color: #ffd460;
        font-size: 1rem;
      }
      p {
        font-weight: 500;
        color: #fff;
        font-size: 1.5rem;
        line-height: 160%;
      }
    }
  }
`;

const ViewBtn = styled.div`
  z-index: 3;
  position: absolute;
  bottom: -50px;
  left: 0;
  display: inline-block;
  width: auto;
  height: 25px;
  /* background: red; */
  margin-top: 40px;
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 3;
    position: relative;
    display: inline-block;
    height: 25px;
    /* background: red; */
    margin-top: 0;
  }

  a {
    z-index: 2;
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 15px;
    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      text-align: center;
      z-index: 2;
      position: relative;
      display: inline-block;
      width: 100%;
      height: 100%;
      font-family: "Montserrat";
      font-weight: 500;
      color: #fff;
      font-size: 15px;
      transition: all 0.3s;
    }
  }
`;

const BtnWrap = styled.ul`
  z-index: 2;
  position: absolute;
  bottom: 30px;
  left: -150px;

  @media ${({ theme }) => theme.device.mobile} {
    z-index: 3;
    position: relative;
    left: 0;
    top: 30px;
    text-align: center;
  }
  li {
    display: inline-block;
    margin: 0 7px;
    a {
      font-family: "Montserrat";
      font-weight: 500;
      color: #b7b7b7;
      font-size: 15px;
    }
  }
`;
const BtnList = styled.li`
  position: relative;
  margin-bottom: 15px;
  @media ${({ theme }) => theme.device.mobile} {
    position: relative;
    display: inline-block;
    margin: 0 2px;
  }
  &:hover {
    color: #000;
    &::after {
      content: "";
      display: block;
      width: 34px;
      height: 1px;
      background: #8e8f9a;

      position: absolute;

      top: 50%;
      left: -15%;

      transition: all 0.3s ease;
    }
  }
  div {
    display: inline-block;
  }
  a {
    z-index: 3;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 80px;
    }
  }
  div {
    width: 34px;
    height: 1px;
    background: transparent;
    margin-bottom: 6px;
    transition: all 0.3s;
    @media ${({ theme }) => theme.device.mobile} {
      display: none;
    }
  }
`;

const BgWrap = styled.ul`
  z-index: 1;
  position: absolute;
  top: 0;
  right: 0;
  width: 50%;
  height: 100%;
  @media ${({ theme }) => theme.device.laptop} {
    z-index: 1;
    position: absolute;
    top: 0;
    right: 0;
    width: 45%;
    height: 100%;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    width: 100%;
  }
`;

const Bg = styled.li`
  z-index: 10;
  position: absolute;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  background: #f9f7f7;
  h4 {
    font-weight: 500;
    color: #fff;
    font-size: 20px;
    font-family: "Montserrat";
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 18px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      color: #050505;
      font-size: 18px;
    }
  }
  h2 {
    width: 40%;
    font-family: "Montserrat";
    font-weight: 600;
    color: #000;
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;
    @media ${({ theme }) => theme.device.laptop} {
      width: 40%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #050505;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
  }
  h3 {
    width: 30%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 18px;
    line-height: 160%;
    margin-top: 40px;
    @media ${({ theme }) => theme.device.laptop} {
      width: 30%;
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 400;
      color: #050505;
      font-size: 18px;
      line-height: 160%;
      margin-top: 30px;
    }
  }
`;
