import styled from "styled-components";

function SectionAbout() {
  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <ViewBtn>
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </ViewBtn>
      </Title>
      <Wrap>
        <SlideView>
          <ProfileWrap>
            <Right>
              <div>
                <h3>EDUCATION</h3>
                <div>
                  <Date>2023-01-09 - 2023-02-28</Date>
                  <Content> 프론트엔드 웹 개발자 양성 과정 - HTML5, CSS3, 자바스크립트(Java Script), 제이쿼리(JQuery), 리액트(React)</Content>
                  <Date>2021.09.07 - 2022.01.25</Date>
                  <Content> (디지털디자인)UI/UX 반응형 웹디자인 & 웹퍼블리셔B 수료</Content>
                  <Date>2008.03 - 2014.10 </Date>
                  <Content>부산외국어대학교 한국어문학부 졸업</Content>
                  <Content> 부전공: 일본어문학</Content>
                </div>
              </div>

              <div>
                <h3>SKILLS</h3>
                <Content>퍼블리싱 : HTML, CSS, jQuery, Xd, Ps </Content>
                <Content>빅데이터 : Tableau, MicroStrategy, powerBI</Content>
              </div>
            </Right>
          </ProfileWrap>
        </SlideView>
      </Wrap>
      <BgWrap>
        <Bg></Bg>
      </BgWrap>
    </Container>
  );
}

export default SectionAbout;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 1000px;
  background: #fff;

  @media ${({ theme }) => theme.device.mobile} {
    height: 1000px;
    margin-top: 80px;
  }
`;

const Title = styled.div`
  z-index: 2;
  position: relative;
  width: 100%;
  max-width: 74%;
  margin: 0 auto;

  @media ${({ theme }) => theme.device.laptop} {
    z-index: 2;
    position: relative;
    width: 100%;
    max-width: 80%;
    margin: 0 auto;
    padding-top: 100px;
  }
  @media ${({ theme }) => theme.device.tablet} {
    padding-top: 50px;
    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.mobile} {
    padding-top: 100px;
    transition: all 0.3s;
    text-align: center;
  }
  h4 {
    font-weight: 500;
    color: #000;
    font-size: 20px;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 18px;
    }

    @media ${({ theme }) => theme.device.mobile} {
      color: #050505;
      font-size: 18px;
    }
  }
  h2 {
    width: 40%;
    font-family: "Montserrat";
    font-weight: 600;
    color: #000;
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;
    @media ${({ theme }) => theme.device.laptop} {
      width: 40%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }

    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #050505;
      font-size: 50px;
      margin-top: 20px;
