import styled from "styled-components";
import { useState, useRef, useEffect } from "react";
function SectionProject() {
  const data = [
    {
      id: 0,
      title: "MOVIELIST",
      content: "https://kimahr.github.io/movie-list/",

      dots: "",
    },
    {
      id: 1,
      title: "TODOLIST",
      content: "enterprising",
      contents: "진취력있는",
      dots: "",
    },
    {
      id: 2,
      title: "PORTFOLIO",
      content: "shrewd",
      contents: "catch on fast",
      dots: "",
    },
  ];

  const delay = 2500;
  const [index, setIndex] = useState(0);

  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

    return () => {
      resetTimeout();
    };
  });
  return (
    <Container id="project">
      <TitleWrap>
        <Title>
          <h4>04</h4>
          <h2>learn react project</h2>
        </Title>
        <TitleBox>
          <h3>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Quaerat nesciunt, unde vel, ipsum nulla quos alias quidem velit vero est sed. Voluptate nihil distinctio maiores architecto
            corrupti, quam suscipit laudantium!
          </h3>
        </TitleBox>
      </TitleWrap>
      <Wrap>
        <ListWrap>
          <SlideWrap>
            <ListBox>
              <a href="https://kimahr.github.io/movie-list/">VIEW</a>
            </ListBox>
            <ListBox>
              <a href="https://kimahr.github.io/todolist/">VIEW</a>
            </ListBox>
            <ListBox>
              <a href="https://kimahr.github.io/instagram/">VIEW</a>
            </ListBox>
          </SlideWrap>
        </ListWrap>
      </Wrap>
    </Container>
  );
}

export default SectionProject;

const Container = styled.div`
  position: relative;
  width: 100%;
`;

const TitleWrap = styled.div`
  z-index: 2;

  width: 100%;
  max-width: 74%;
  margin: 0 auto;
  padding-top: 100px;
  display: flex;
  justify-content: space-between;

  @media ${({ theme }) => theme.device.laptop} {
    z-index: 2;
    position: relative;

    width: 100%;
    max-width: 80%;
    margin: 0 auto;
    padding-top: 200px;
    display: flex;
    justify-content: space-between;
  }
  @media ${({ theme }) => theme.device.tablet} {
    width: 100%;
    max-width: 80%;
    display: flex;
    justify-content: space-between;
  }
  @media ${({ theme }) => theme.device.mobile} {
    display: flex;
    flex-direction: column;
    padding-top: 100px;
  }
`;
const Title = styled.div`
  width: 50%;
  display: flex;
  flex-direction: column;

  @media ${({ theme }) => theme.device.laptop} {
    width: 100%;
  }
  h4 {
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 20px;
    @media ${({ theme }) => theme.device.mobile} {
      text-align: center;
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
    }
  }
  h2 {
    width: 100%;
    font-family: "Montserrat";
    font-weight: 600;
    color: #000;
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;

    @media ${({ theme }) => theme.device.tablet} {
      width: 100%;
      font-family: "Montserrat";
      font-weight: 600;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
    @media ${({ theme }) => theme.device.mobile} {
      text-align: center;
      width: 100%;
      font-family: "Montserrat";
      font-weight: 700;
      color: #000;
      font-size: 50px;
      margin-top: 20px;
      line-height: 120%;
    }
  }
`;

const TitleBox = styled.div`
  width: 50%;
  @media ${({ theme }) => theme.device.mobile} {
    width: 100%;
    text-align: center;
  }
  h3 {
    width: 100%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 18px;
    line-height: 160%;
    margin-top: 60px;
    /* @media ${({ theme }) => theme.device.laptop} {
      width: 70%;
    } */
    @media ${({ theme }) => theme.device.mobile} {
      text-align: center;
      width: 100%;
      font-family: "Montserrat";
      font-weight: 500;
      color: #000;
      font-size: 18px;
      line-height: 160%;
      margin-top: 40px;
    }
  }
`;

const Wrap = styled.div`
  position: relative;
  width: 100%;
  margin: 200px auto;
  /* padding-top: 70px; */
  overflow: hidden;
  @media ${({ theme }) => theme.device.mobile} {
    margin: 100px auto;
  }
`;

const ListWrap = styled.div`
  width: 100%;
  max-width: 74%;
  margin: 0 auto;
  height: 100%;

  @media ${({ theme }) => theme.device.laptop} {
    width: 100%;
    max-width: 80%;
    margin: 0 auto;
  }

  ul {
    width: 100%;
    height: 500px;
    display: flex;
    text-align: center;
    @media ${({ theme }) => theme.device.laptop} {
      width: 100%;
      height: 350px;
    }
    @media ${({ theme }) => theme.device.mobile} {
      width: 100%;
      height: 400px;
      display: flex;
      flex-direction: column;
    }
  }
`;

const SlideWrap = styled.ul`
  position: relative;
  width: 80%;
  margin: 30% auto;
  /* top: 150px; */
  @media ${({ theme }) => theme.device.mobile} {
    z-index: 2;
    position: absolute;
    top: 150px;
    left: 0;
    text-align: center;
    width: 100%;
  }
`;

const ListBox = styled.li`
  width: 33.333%;
  height: 100%;

  margin-left: 20px;
  @media ${({ theme }) => theme.device.mobile} {
    width: 100%;
    margin-left: 0;
    margin-bottom: 20px;
  }

  border: 1px solid black;
  transition: all 0.3s;
  &:first-child {
    margin-left: 0;
  }
  &:hover {
    border-radius: 10%;
    @media ${({ theme }) => theme.device.laptop} {
      border-radius: 10%;
    }
    @media ${({ theme }) => theme.device.mobile} {
      border-radius: 0;
      background-color: #f9f7f7;
      cursor: pointer;
    }
  }
`;
