import styled from "styled-components";
import { useEffect, useState } from "react";
import SectionAbout from "./section/SectionAbout";
import SectionMain from "./section/SectionMain";
import SectionDevelop from "./section/SectionDevelop";
import Animation from "./section/Animation";
import SectionProject from "./section/SectionProject";
import SectionClose from "./section/SectionClose";

import SectionSkill from "./section/SectionSkill";
import TopBtn from "./section/TopBtn";

function Main() {
  const [ScrollY, setScrollY] = useState(0);
  const [BtnStatus, setBtnStatus] = useState(false); // 버튼 상태

  const handleFollow = () => {
    setScrollY(window.pageYOffset);
    if (ScrollY > 100) {
      // 100 이상이면 버튼이 보이게
      setBtnStatus(true);
    } else {
      // 100 이하면 버튼이 사라지게
      setBtnStatus(false);
    }
  };

  const handleTop = () => {
    // 클릭하면 스크롤이 위로 올라가는 함수
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
    setScrollY(0); // ScrollY 의 값을 초기화
    setBtnStatus(false); // BtnStatus의 값을 false로 바꿈 => 버튼 숨김
  };

  useEffect(() => {
    const watch = () => {
      window.addEventListener("scroll", handleFollow);
    };
    watch();
    return () => {
      window.removeEventListener("scroll", handleFollow);
    };
  });

  return (
    <Container>
      <Wrap>
        <button>TOP</button>
      </Wrap>
      <SectionMain />
      <SectionSkill />
      <SectionAbout />
      <SectionDevelop />
      <Animation />
      <SectionProject />
      <SectionClose />
    </Container>
  );
}

export default Main;

const Container = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
`;

const Wrap = styled.div`
  position: relative;
  padding: 30px;
  font-size: 18px;
  line-height: 1.6;
  background: lightgray;
  button {
    position: fixed;
    opacity: 0;
    bottom: 40px;
    right: 40px;
    z-index: -10;
    width: 50px;
    height: 50px;
    border-radius: 100%;
    border: 0 none;
    background: lightpink;
    color: blueviolet;
    border: 2px solid blueviolet;
    font-size: 18px;
    font-weight: bold;
    letter-spacing: -0.06em;
    box-shadow: 1px 1px 6px 3px rgba(0, 0, 0, 0.3);
    cursor: pointer;
    transition: opacity 0.3s ease-in;
    &:active {
      z-index: 10;
      opacity: 1;
    }
    &:hover,
    &:focus,
    /* &:active {
      outline: 0 none;
    } */
  }
`;
