import React from "react";
import styled from "styled-components";
import Burger from "./Burger";
import { useState, useEffect } from "react";
const Navbar = () => {
  const [scroll, setScroll] = useState(0);
  const updateScroll = () => {
    setScroll(window.scrollY || document.documentElement.scrollTop);
  };
  useEffect(() => {
    window.addEventListener("scroll", updateScroll);
  });

  return (
    <Container scroll={scroll}>
      <Wrap>
        <Gnb>
          <LogoWrap>
            <Logo>ahreum</Logo>
          </LogoWrap>
          <NavWrap>
            <Nav>
              <ul>
                <li>
                  <a href="#about">ABOUT</a>
                  <span></span>
                </li>
                <li>
                  <a href="#skills">SKILLS</a>
                  <span></span>
                </li>
                <li>
                  <a href="#develop">DEVELOP</a>
                  <span></span>
                </li>
                <li>
                  <a href="#project">PROJECT</a>
                  <span></span>
                </li>
              </ul>
            </Nav>
          </NavWrap>
          <RightNavWrap scroll={scroll}>
            <Burger />
          </RightNavWrap>
        </Gnb>
      </Wrap>
    </Container>
  );
};

const Container = styled.header`
  width: 100%;
  height: 100px;
  line-height: 100px;
  position: fixed;
  background: ${({ scroll }) => (scroll > 500 ? "#fff" : "#112D4E")};

  color: ${({ scroll }) => (scroll > 100 ? "#fff" : "#F9F7F7")};

  font-size: ${({ theme }) => theme.fonts.fontSmall};
  font-weight: ${({ theme }) => theme.fonts.weightBold};

  top: 0;
  z-index: 100;
  @media ${({ theme }) => theme.device.laptop} {
    position: relative;

    height: 100%;

    transition: all 0.3s;
  }
  @media ${({ theme }) => theme.device.tablet} {
    z-index: 50;
  }
`;

const Wrap = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;

  @media ${({ theme }) => theme.device.laptop} {
  }
`;
const Gnb = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;

  /* position: relative; */
  @media ${({ theme }) => theme.device.laptop} {
    transition: all 0.3s;
    padding: 0 20px;
    position: fixed;
  }
`;

const LogoWrap = styled.div`
  width: 10%;
`;
const Logo = styled.a`
  font-weight: 600;
  font-size: 22px;

  @media ${({ theme }) => theme.device.laptop} {
    font-weight: 600;
    font-size: 22px;
    /* 
    color: #fff; */
  }
`;

const NavWrap = styled.div`
  width: 100%;
  /* position: fixed; */
`;

const Nav = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: space-around;
  align-items: center;

  @media ${({ theme }) => theme.device.tablet} {
    display: none;
  }

  ul {
    width: 60%;
    display: flex;
    justify-content: space-evenly;
    align-items: center;

    li {
      font-size: 15px;
      font-weight: ${({ theme }) => theme.colors.weightSemiBold};

      a {
        color: ${({ scroll }) => (scroll > 100 ? "#fff" : "#F9F7F7")};
      }
    }
  }
`;

const RightNavWrap = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  /* background: ${({ scroll }) => (scroll > 100 ? "#fff" : "#112D4E")}; */
  @media ${({ theme }) => theme.device.laptop} {
    right: 40px;
  }
`;

export default Navbar;
