import styled from "styled-components";
import { useState } from "react";

function SectionSkills() {
  const data = [
    {
      id: 0,
      title: "html",
      content: "html",
    },
    {
      id: 1,
      title: "css",
      content: "css",
    },
    {
      id: 2,
      title: "JavaScript",
      content: "JavaScript",
    },
    {
      id: 3,
      title: "react",
      content: "react",
    },
    {
      id: 4,
      title: "photoshop",
      content: "photoshop",
    },
    {
      id: 5,
      title: "xd",
      content: "xd",
    },
    {
      id: 6,
      title: "figma",
      content: "figma",
    },
    {
      id: 7,
      title: "github",
      content: "github",
    },
  ];

  const [index, setIndex] = useState(0);

  return (
    <Container>
      <Title>
        <h2>skill</h2>
      </Title>
      <ListWrap>
        <SlideList>
          {data.map((item) => (
            <li
              key={item.id}
              className={index === item.id ? SlideTextWrap.active : null}
              onClick={() => setIndex(item.id)}
            >
              {item.title}
              <span></span>
            </li>
          ))}
        </SlideList>
      </ListWrap>
      <SlideTextWrap>
        {data
          .filter((item) => index === item.id)
          .map((item) => (
            <SlideText>
              {item.content}
              <span></span>
            </SlideText>
          ))}
        <TextBox></TextBox>
      </SlideTextWrap>
    </Container>
  );
}
export default SectionSkills;

const Container = styled.div`
  position: relative;

  width: 100%;
  height: 43vw;
  max-width: 1400px;
  margin: 0 auto;
  background-color: ${({ theme }) => theme.colors.white_color};
`;

const Title = styled.div`
  margin-top: 140px;

  h2 {
    font-weight: ${({ theme }) => theme.fonts.weightExtra};
    color: ${({ theme }) => theme.colors.main_color};
  }
`;

const ListWrap = styled.div`
  width: 100%;
  height: 100%;

  display: flex;
  justify-content: space-around;
`;

const SlideList = styled.ul`
  width: 50%;
  height: 50%;

  position: relative;

  display: flex;
  flex-direction: column;
  align-items: flex-start;

  li {
    width: 50%;
    height: 300px;

    position: relative;

    span {
      position: absolute;

      &::after {
        content: "";
        display: block;
        width: 34px;
        height: 1px;

        background: #000;
      }
      &:hover {
        &::after {
          content: "";
          display: block;
          width: 34px;
          height: 1px;

          position: absolute;
          left: -10%;
          bottom: 50%;

          background: #000;
          transition: all 0.3s ease;
        }
      }
    }
  }
`;

const SlideTextWrap = styled.ul`
  z-index: 2;
  /* position: absolute;
  width: 100%; */
  top: 20%;
  right: 30%;
`;

const SlideText = styled.li`
  position: absolute;
  display: inline-block;
  top: -10%;
  left: 50%;

  height: 300px;
  background: #fff;
  color: ${({ theme }) => theme.colors.main_color};
  font-size: ${({ theme }) => theme.fonts.fontExtra};
  font-weight: ${({ theme }) => theme.fonts.weightExtra};

  span {
    z-index: 1;
    display: inline-block;
    position: absolute;

    top: 35%;
    left: 0;

    width: 100%;
    height: 7%;
    background: #5053582c;
    transition: all 0.3s;
    overflow: hidden;

    &::before {
      content: "";
      position: absolute;
      top: 0;
      left: 0%;
      width: 0%;
      height: 100%;
      background: #0066ff;
      transition: all 0.3s;
    }
    &:hover {
      background: #0066ff;
      &::before {
        width: 100%;
      }
    }
  }
`;

const TextBox = styled.div`
  position: absolute;
  top: 10%;
  right: 0;
  width: 50%;
  height: 50%;
  background-color: #2d4059;
  background-color: ${({ theme }) => theme.colors.orange_color};
`;
