import styled, { keyframes } from "styled-components";
import { useState, useRef, useEffect } from "react";

const SectionSkill = () => {
  const data = [
    {
      id: 0,
      title: "html",
      content: "html",
      contents: "1. html ",
    },
    {
      id: 1,
      title: "css",
      content: "css",
      contents: "2.css ",
    },
    {
      id: 2,
      title: "JavaScript",
      content: "JavaScript",
      contents: "3.JavaScript ",
    },
    {
      id: 3,
      title: "react",
      content: "react",
      contents: "4. react",
    },
    {
      id: 4,
      title: "photoshop",
      content: "photoshop",
      contents: "5.photoshop ",
    },
    {
      id: 5,
      title: "xd",
      content: "xd",
      contents: "6.xd ",
    },
    {
      id: 6,
      title: "figma",
      content: "figma",
      contents: "7.figma ",
    },
    {
      id: 7,
      title: "github",
      content: "github",
      contents: "8. github",
    },
  ];

  const delay = 2500;
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

    return () => {
      resetTimeout();
    };
  });
  return (
    <Container>
      <Slide>
        <SlideWrap>
          {data
            .filter((item) => index === item.id)
            .map((item) => (
              <SlideList>
                <SlideTxt>
                  <a href="#">{item.content}</a>
                  <span></span>
                  <h4>a revelatory insight</h4>
                </SlideTxt>
              </SlideList>
            ))}
        </SlideWrap>
      </Slide>
      <BtnWrap>
        {data.map((item) => (
          <BtnList key={item.id} className={index === item.id ? SlideWrap.active : null} onClick={() => setIndex(item.id)} style={{ transform: `translate3d(${index}%)` }}>
            <BtnTxt>{item.title}</BtnTxt>
            {data.map((index) => (
              <div key={index}></div>
            ))}
          </BtnList>
        ))}
      </BtnWrap>
      <BgWrap>
        <Bg></Bg>
        <Txt>
          <p>words that express me</p>
        </Txt>
      </BgWrap>
    </Container>
  );
};
export default SectionSkill;

const Container = styled.div`
  width: 100%;
  height: 50vw;
  max-width: 1400px;
  margin: 0 auto;
  position: relative;
`;

const Slide = styled.div`
  z-index: 1;
  position: relative;
  width: 100%;
  height: 969px;
`;

const SlideWrap = styled.ul`
  position: relative;
  width: 100%;
`;

const SlideList = styled.li`
  position: absolute;
  top: 0;
  left: 50%;
  width: 50%;
  height: 300px;
  background: #fff;
`;

const bgChange = keyframes`
-100% {
  background: #5053582c;

}

0%{
  background:  #0066ff;
 
}
`;

const SlideTxt = styled.div`
  position: relative;
  display: inline-block;

  a {
    z-index: 2;
    position: relative;
    font-weight: 700;
    color: #000;
    font-size: 100px;
    transition: all 0.3s;
  }
  span {
    z-index:1;
    padding: 15px 100px;
    margin: 10px 4px;
    color:  #5053582c;
    font-family: sans-serif;
    text-transform: uppercase;
    text-align: center;
    position: relative;
    text-decoration: none;
    display: inline-block;
    /* z-index: 1;
    display: inline-block;
    position: absolute;
    bottom: 50px;
    left: 0;
    width: 100%;
    height: 20px;
    background: #5053582c;
    transition: all 0.7s;
    overflow: hidden; */
    transform: scaleX(1, 0);
    &::before {
      content: "";
      position: absolute;
      left: 0px;
      bottom: 0px;
      z-index: -1;
      width: 100%;
      height: 100%;
      background:  #5053582c;
      box-shadow: inset 0px 0px 0px #6098ff;
      display: block;
      transition: all 0.4s cubic-bezier(.5, .24, 0, 1)
      transform: scale(0, 1);
    }
  }
    &:hover { &::before {
      box-shadow: inset 300px 0px 0px #6098ff;
    }
  }

  h4 {
    margin-top: 20px;
    font-weight: 500;
    color: #000;
    font-size: 26px;
  }
`;

const BtnWrap = styled.ul`
  z-index: 2;
  position: absolute;
  top: 20%;
  left: 30%;
`;

const BtnList = styled.li`
  position: relative;
  margin-bottom: 15px;
  &:hover {
    color: #000;
    &::after {
      content: "";
      display: block;
      width: 34px;
      height: 1px;
      background: #8e8f9a;

      position: absolute;

      top: 50%;
      left: -15%;

      transition: all 0.3s ease;
    }
  }
  div {
    display: inline-block;
  }
  a {
    z-index: 3;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
  }
  div {
    width: 34px;
    height: 1px;
    background: transparent;
    margin-bottom: 6px;
    transition: all 0.3s;
  }
`;

const BtnTxt = styled.div`
  font-weight: 500;
  color: #8e8f9a;
  font-size: 18px;
  &:hover {
    color: #000;
  }
`;

const BgWrap = styled.div`
  position: absolute;
  top: 250px;
  right: 0;
  width: 50%;
  height: 100%;
`;

const Bg = styled.div`
  z-index: 1;
  position: absolute;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  background: #474859;
`;

const Txt = styled.div`
  z-index: 2;
  position: absolute;
  top: 0;
  right: 26.4319496%;
  width: 100%;

  p {
    text-align: right;
    font-family: "Montserrat";
    font-weight: 500;
    color: #fff;
    font-size: 20px;
    padding-top: 50px;
  }
`;
