import React from "react";
import styled from "styled-components";
import Burger from "./Burger";
import { useState, useEffect } from "react";

const Navbar = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const updateScroll = () => {
    setScrollPosition(window.scrollY || document.documentElement.scrollTop);
  };
  useEffect(() => {
    window.addEventListener("scroll", updateScroll);
  });
  return (
    <Container>
      <Wrap scroll={scrollPosition}>
        <Gnb>
          <LogoWrap>
            <Logo>ahreum</Logo>
          </LogoWrap>
          <NavWrap>
            <Nav>
              <ul>
                <li>
                  <a href="#about">ABOUT</a>
                </li>
                <li>
                  <a href="#about">SKILLS</a>
                </li>
                <li>
                  <a href="#develop">DEVELOP</a>
                </li>
                <li>
                  <a href="#project">PROJECT</a>
                </li>
              </ul>
            </Nav>
          </NavWrap>
          <RightNavWrap>
            <Burger />
          </RightNavWrap>
        </Gnb>
      </Wrap>
    </Container>
  );
};

const Container = styled.header`
  width: 100%;
  height: 100px;
  line-height: 100px;
  background-color: ${({ scrollPosition }) => (scrollPosition ? "#fff" : "#112D4E")};
  color: ${({ scrollPosition }) => (scrollPosition ? "#FFF" : "#112D4E")};
  opacity: ${({ scrollPosition }) => (scrollPosition ? "0.5" : "1")};
  font-size: ${({ theme }) => theme.fonts.fontMicro};
  font-weight: ${({ theme }) => theme.fonts.weightBold};
  position: fixed;
  top: 0;
  z-index: 100;
`;

const Wrap = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
`;
const Gnb = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  overflow: hidden;
`;

const LogoWrap = styled.div`
  width: 10%;
`;
const Logo = styled.h1`
  font-weight: 600;
  font-size: 22px;
  color: #fff;
`;

const NavWrap = styled.div`
  width: 80%;
`;

const Nav = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: flex-end;
  align-items: center;

  ul {
    width: 60%;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    li {
      font-size: 15px;
      font-weight: ${({ theme }) => theme.colors.weightSemiBold};
      a {
        color: ${({ theme }) => theme.colors.white_color};
      }
    }
  }
`;

const RightNavWrap = styled.div`
  width: 10%;
  position: absolute;
  right: 0;
`;

export default Navbar;
