import { useState, useEffect, useRef } from "react";
import styled from "styled-components";

function SectionProject() {
  const text = ["KEEP GOING KEEP GOING"];
  let slides = setSlides();
  const 양끝에_추가될_데이터수 = 2;

  function setSlides() {
    let addedFront = [];
    let addedLast = [];
    var index = 0;
    while (index < 양끝에_추가될_데이터수) {
      addedLast.push(items[index % items.length]);
      addedFront.unshift(items[items.length - 1 - (index % items.length)]);
      index++;
    }
    return [...addedFront, ...items, ...addedLast];
  }

  function replaceSlide(index) {
    setTimeout(() => {
      setTransition("");
      setCurrentIndex(index);
    }, transitionTime);
  }

  function handleSwipe(direction) {
    let index = currentIndex + direction;
    setCurrentIndex(index);
    if (index < 양끝에_추가될_데이터수) {
      index += itemSize;
      replaceSlide(index);
    } else if (index >= itemSize + 양끝에_추가될_데이터수) {
      index -= -itemSize;
      replaceSlide(index);
    }
    setTransition(transitionStyle);
  }

  return (
    <Container>
      <Wrap>
        <TextBannerWrap>
          <TextBanner
            style={{
              transform: `translateX(${
                (-100 / slides.length) * (0.5 + currentIndex)
              }%)`,
              transition: slideTransition,
            }}
          >
            <Text></Text>
          </TextBanner>
        </TextBannerWrap>
      </Wrap>
    </Container>
  );
}

export default SectionProject;

const Container = styled.div`
  width: 100%;
`;

const Wrap = styled.div`
  width: 100%;
`;

const TextBannerWrap = styled.div`
  overflow: hidden;
  width: 100%;
`;

const TextBanner = styled.div`
  white-space: nowrap;
  transition: ease 1000ms;
`;

const Text = styled.div`
  display: inline-block;
  width: 100%;
  font-weight: ${({ theme }) => theme.fonts.weightExtra};
  font-size: 110px;
`;
