import styled from "styled-components";
import { useState, useRef, useEffect } from "react";

const SectionSkill = () => {
  const data = [
    {
      id: 0,
      title: "html",
      content: "html",
      contents: "1. html ",
    },
    {
      id: 1,
      title: "css",
      content: "css",
      contents: "2.css ",
    },
    {
      id: 2,
      title: "JavaScript",
      content: "JavaScript",
      contents: "3.JavaScript ",
    },
    {
      id: 3,
      title: "react",
      content: "react",
      contents: "4. react",
    },
    {
      id: 4,
      title: "photoshop",
      content: "photoshop",
      contents: "5.photoshop ",
    },
    {
      id: 5,
      title: "xd",
      content: "xd",
      contents: "6.xd ",
    },
    {
      id: 6,
      title: "figma",
      content: "figma",
      contents: "7.figma ",
    },
    {
      id: 7,
      title: "github",
      content: "github",
      contents: "8. github",
    },
  ];

  const delay = 2500;
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) => (prevIndex === data.length - 1 ? 0 : prevIndex + 1)), delay);

    return () => {
      resetTimeout();
    };
  });
  return (
    <Container>
      <Slide>
        <SlideWrap>
          <SlideList>
            <div class="s2Slide_txt">
              <a href="./sub/about.php" class="s2SlideBtn">
                insight
              </a>
              <span></span>
              <h4>a revelatory insight</h4>
            </div>
          </SlideList>
          <SlideList>
            <SlideTxt>
              <a href="./sub/about.php" class="s2SlideBtn">
                insight
              </a>
              <span></span>
              <h4>a revelatory insight</h4>
            </SlideTxt>
          </SlideList>
          <SlideList>
            <SlideTxt>
              <a href="./sub/about.php" class="s2SlideBtn">
                insight
              </a>
              <span></span>
              <h4>a revelatory insight</h4>
            </SlideTxt>
          </SlideList>
        </SlideWrap>
      </Slide>
      <BtnWrap>
        {data
          .filter((item) => index === item.id)
          .map((item) => (
            <li>
              <Line></Line>
              <One></One>
              <BtnTxt>insight</BtnTxt>
              <a></a>
            </li>
          ))}
      </BtnWrap>
      <BgWrap>
        <Bg></Bg>
        <Txt>
          <p>words that express me</p>
        </Txt>
      </BgWrap>
    </Container>
  );
};
export default SectionSkill;

const Container = styled.div`
  width: 100%;
  height: 50vw;
  max-width: 1400px;
  margin: 0 auto;
  position: relative;
`;

const Slide = styled.div`
  z-index: 1;
  position: relative;
  width: 100%;
  height: 969px;
`;

const SlideWrap = styled.ul`
  position: relative;
  width: 100%;
`;

const SlideList = styled.li`
  position: absolute;
  top: 0;
  left: 50%;
  width: 50%;
  height: 300px;
  background: #fff;
`;

const SlideTxt = styled.div`
  position: relative;
  display: inline-block;

  a {
    z-index: 2;
    position: relative;
    font-family: "Montserrat";
    font-weight: 700;
    color: #000;
    font-size: 100px;
  }
  span {
    z-index: 1;
    display: inline-block;
    position: absolute;
    bottom: 50px;
    left: 0;
    width: 100%;
    height: 20px;
    background: #5053582c;
    transition: all 0.3s;
    overflow: hidden;
    ::before {
      content: "";
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: #0066ff;
      transition: all 0.3s;
    }
    h4 {
      margin-top: 20px;

      font-weight: 500;
      color: #000;
      font-size: 26px;
    }
  }
`;

const BtnWrap = styled.ul`
  z-index: 2;
  position: absolute;
  top: 20%;
  left: 30%;
  li {
    position: relative;
  }
  a {
    z-index: 3;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
  }
`;

const Line = styled.div`
  width: 34px;
  height: 1px;
  background: transparent;
  margin-bottom: 6px;
  transition: all 0.3s;
`;

const One = styled.div`
  display: none;
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: #8e8f9a;
  margin-bottom: 1px;
`;

const BtnTxt = styled.div`
  font-weight: 500;
  color: #8e8f9a;
  font-size: 18px;
`;

const BgWrap = styled.div`
  position: absolute;
  top: 250px;
  right: 0;
  width: 50%;
  height: 100%;
`;

const Bg = styled.div`
  z-index: 1;
  position: absolute;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  background: #474859;
`;

const Txt = styled.div`
  z-index: 2;
  position: absolute;
  top: 0;
  right: 26.4319496%;
  width: 100%;

  p {
    text-align: right;
    font-family: "Montserrat";
    font-weight: 500;
    color: #fff;
    font-size: 20px;
    padding-top: 50px;
  }
`;
