import styled from "styled-components";

function SectionAbout() {
  return (
    <Container>
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <BtnWrap>
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </BtnWrap>
      </Title>
      <Wrap>
        <div class="s3Slide_view">
          <ul class="s3Slide_wrap">
            <li class="s3Slide s3Slide0">
              <div></div>
            </li>
            <li class="s3Slide s3Slide1">
              <div></div>
            </li>
            <li class="s3Slide s3Slide2">
              <div></div>
            </li>
          </ul>
        </div>
        <ul class="s3PageBtn_wrap">
          <li class="adds3Page">
            <a href="javascript:" class="s3PageBtn">
              01
            </a>
          </li>
          <li class="">
            <a href="javascript:" class="s3PageBtn">
              02
            </a>
          </li>
          <li class="">
            <a href="javascript:" class="s3PageBtn">
              03
            </a>
          </li>
        </ul>
      </Wrap>
      <div class="bg_wrap">
        <div class="bg"></div>
      </div>
    </Container>
  );
}

export default SectionAbout;

const Container = styled.div`
  z-index: 1;
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 540px;
  background: #fff;
  margin-top: 130px;

  @media ${({ theme }) => theme.device.laptop} {
    display: flex;
    flex-direction: column;
  }
`;

const Wrap = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  @media ${({ theme }) => theme.device.laptop} {
  }
`;

const Title = styled.div`
  z-index: 2;
  position: relative;
  width: 100%;
  max-width: 74%;
  margin: 0 auto;
  padding-top: 100px;
  h4 {
    font-weight: 500;
    color: #000;
    font-size: 18px;
  }
  h2 {
    width: 40%;
    font-family: "Montserrat";
    font-weight: 600;
    color: #000;
    font-size: 60px;
    margin-top: 20px;
    line-height: 120%;
  }
  h3 {
    width: 30%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 18px;
    line-height: 160%;
    margin-top: 40px;
  }
  @media ${({ theme }) => theme.device.laptop} {
    margin: 0 20px;
  }
  h2 {
    font-weight: 800;
    color: #000;
    font-size: 28px;
    @media ${({ theme }) => theme.device.laptop} {
      font-size: 26px;
    }
  }
`;

const BtnWrap = styled.div`
  z-index: 3;
  position: absolute;
  bottom: -50px;
  left: 0;
  display: inline-block;
  width: auto;
  height: 25px;
  /* background: red; */
  margin-top: 40px;

  a {
    z-index: 2;
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    font-family: "Montserrat";
    font-weight: 500;
    color: #000;
    font-size: 15px;
    transition: all 0.3s;
  }
`;
