import styled from "styled-components";
import { useState, useEffect } from "react";

function SectionClose() {
  const dogRef = useRef(null);
  const bgRef = useRef(null);
  const text3dRef = useRef(null);
  const pipeRef = useRef(null);

  let x = 0;
  let y = 0;
  let mx = 0;
  let my = 0;

  const handleMouseMove = (e) => {
    x = e.clientX - window.innerWidth / 2;
    y = e.clientY - window.innerHeight / 2;
  };

  const loop = () => {
    const dog = dogRef.current;
    const bg = bgRef.current;
    const text3d = text3dRef.current;
    const pipe = pipeRef.current;
    const speed = 0.009;

    mx += (x - mx) * speed;
    my += (y - my) * speed;
    dog.style.transform = `translate(${mx / 9}px, ${my / 9}px)`;

    bg.style.transform = `translate(${mx / 8}px, ${-(my / 8)}px)`;
    text3d.style.transform = `translate3d(${-(mx / 5)}px,${-(
      my / 5
    )}px,0) rotate3d(0,1,0,${-mx / 50}deg)`;
    pipe.style.transform = `translate(${mx / 4}px, ${my / 3}px)`;

    window.requestAnimationFrame(loop);
  };

  useEffect(() => {
    window.addEventListener("mousemove", handleMouseMove);

    loop();

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);
  return (
    <Container>
      <Wrap>
        <ImgList>
          <Img01>
            <img className="cilcle_01" alt="circle_bg" src="img/Ellipse5.png" />
          </Img01>

          <Img02>
            <img className="cilcle_02" alt="circle_bg" src="img/Ellipse6.png" />
          </Img02>
          <Img03>
            <img className="cilcle_03" alt="circle_bg" src="img/Ellipse9.png" />
          </Img03>
          <Img04>
            <img className="cilcle_04" alt="circle_bg" src="img/Ellipse8.png" />
          </Img04>
        </ImgList>
        <TextBox>
          <p>Thank you for taking your time for my portfolio. </p>
        </TextBox>
      </Wrap>
    </Container>
  );
}
export default SectionClose;

const Container = styled.div`
  width: 100%;
  height: 40vw;

  margin: 0 auto;

  background-color: ${({ theme }) => theme.colors.gray_color};

  position: relative;
`;

const Wrap = styled.div`
  width: 60%;
  height: 100%;
  margin: 10% auto;

  display: flex;
  text-align: center;
  justify-content: center;
  align-items: center;
`;

const TextBox = styled.div`
  position: absolute;
  z-index: 3;
  p {
    width: 50%;
    margin: 0 auto;
    font-size: ${({ theme }) => theme.fonts.fontExtra};
    font-weight: ${({ theme }) => theme.fonts.weightSemiRegular};
  }
`;

const ImgList = styled.div`
  top: 50%;
  left: -50%;
  z-index: 1;
`;

const Img01 = styled.span`
  position: absolute;
  left: 35%;
  bottom: 20%;
`;

const Img02 = styled.span`
  position: absolute;
  top: 20%;
  right: 5%;
`;
const Img03 = styled.span`
  position: absolute;
  top: 40%;
  right: 0;
`;
const Img04 = styled.span`
  position: absolute;
  bottom: 20;
  left: 0;
`;
