import styled from "styled-components";

function SectionAbout() {
  return (
    <Container id="skills">
      <Title>
        <h4>02</h4>
        <h2>Various ways for visual expression</h2>
        <h3>We always think about what could be the best way for visual expressions. We try to explore as many avenues as possible for better visual representation. </h3>
        <ViewBtn>
          <a href="./sub/drawing.php" class="s3ViewBtn">
            VIEW
          </a>
          <span></span>
        </ViewBtn>
      </Title>
      <Wrap>
        <SlideView>
          <ProfileWrap>
            <Left>
              <h2>PROFILE</h2>
              {/* <img src="/img/kim_img.jpg" /> */}
              <p>김아름</p>
              <p>서울 마포구 거주</p>
              <p>1989.03.07 </p>
              <p>010.2967.5037</p>
              <p>dkfma0307@naver.com</p>
            </Left>
            <Right>
              <div>
                <h3>EDUCATION</h3>
                <Content>
                  <Date>2021.09.07 - 2022.01.25</Date>
                  <Content> (디지털디자인)UI/UX 반응형 웹디자인&amContent;웹퍼블리셔B 수료</Content>
                  <Date>2008.03 - 2014. 10 </Date>
                  <Content>부산외국어대학교 한국어문학부 졸업</Content>
                  <Content> 부전공: 일본어문학</Content>
                </Content>
              </div>
              <div>
                <h3>CAREER</h3>
                <Content>
                  <Date>2019.07 - 2021.08</Date>
                  <Content> (주)단비 빅데이터팀 / 빅데이터 시각화</Content>
                </Content>
              </div>
              <div>
                <h3>SKILLS</h3>
                <Content>
                  <Content>퍼블리싱 : HTML, CSS, jQuery, Xd, Ps </Content>
                  <p>빅데이터 : Tableau, MicroStrategy, powerBI</p>
                </Content>
              </div>
            </Right>
          </ProfileWrap>
        </SlideView>
        {/* <BtnWrap>
          {data.map((item) => (
