import React from "react";
import styled from "styled-components";

const RightNav = ({ open }) => {
  return (
    <Container>
      <Ul open={open}>
        <li>
          <Num>01</Num>
          <TxtWrap>
            <a href="./index.php" class="mainBtn_m">
              ABOUT
            </a>
            <span></span>
          </TxtWrap>
        </li>
        <li class="addNav_m nav_m1">
          <Num>01</Num>
          <TxtWrap>
            <a href="./sub/design_uiux.php" class="mainBtn_m">
              SKILLS
            </a>
            <span></span>
          </TxtWrap>
        </li>
        <li>
          <Num>01</Num>
          <TxtWrap>
            <a href="./sub/develop.php" class="mainBtn_m">
              DEVELOP
            </a>
            <span></span>
          </TxtWrap>
        </li>
        <li>
          <Num>01</Num>
          <TxtWrap>
            <a href="./sub/about.php" class="mainBtn_m">
              PROJECT
            </a>
            <span></span>
          </TxtWrap>
        </li>
      </Ul>
    </Container>
  );
};
const Container = styled.div`
  width: 100%;
  height: 100px;
  line-height: 100px;
  background-color: ${({ theme }) => theme.colors.main_color};
  color: ${({ theme }) => theme.fonts.font_color};
  font-size: ${({ theme }) => theme.fonts.fontMicro};
  font-weight: ${({ theme }) => theme.fonts.weightBold};
`;
const Ul = styled.ul`
  flex-flow: column nowrap;
  background-color: #0d2538;
  position: fixed;
  transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
  top: 0;
  right: 0;
  height: 100vh;
  width: 100%;
  padding-top: 3.5rem;
  transition: transform 0.3s ease-in-out;
  li {
    padding: 18px 10px;
    color: #fff;
  }
  @media (max-width: 768px) {
    flex-flow: column nowrap;
    background-color: #0d2538;
    position: fixed;
    transform: ${({ open }) => (open ? "translateX(0)" : "translateX(100%)")};
    top: 0;
    right: 0;
    height: 100vh;
    width: 300px;
    padding-top: 3.5rem;
    transition: transform 0.3s ease-in-out;
    li {
      color: #fff;
    }
  }
`;

const Num = styled.div`
  display: inline-block;
  position: relative;
  bottom: 15px;
  font-weight: 500;
  font-size: 20px;
`;

const TxtWrap = styled.div`
  position: relative;
  display: inline-block;
  margin-left: 20px;

  a {
    z-index: 2;
    position: relative;
    display: inline-block;
    font-weight: 500;
    font-size: 60px;
    color: #fff;
  }
  span {
    z-index: 1;
    position: absolute;
    bottom: 0;
    left: 0;
    display: inline-block;
    width: 100%;
    height: 17px;

    transition: all 0.3s;
    /* transform: scale(0, 1); */
    &:hover {
      background: #0066ff;
      transition: all 0.3s;
      transform: scale(0, 1);
    }
  }
`;
export default RightNav;
