import React, { useState } from "react";
import styled from "styled-components";
import RightNav from "./RightNav";
import { useEffect } from "react";

const Burger = () => {
  const [open, setOpen] = useState(false);

  useEffect(() => {
    const scroll = window.scrollY;
    documnet.body.style.cssText = `
      position: fixed;
      top: -${scroll}px;
      overflow: hidden;
      width: 100%;
      height: auto;
    `;
    return () => {
      document.body.style.cssText = "";
      window.scrollTo(0, scroll);
    };
  }, []);

  return (
    <>
      <StyledBurger open={open} onClick={() => setOpen(!open)}>
        <div />
        <div />
        <div />
      </StyledBurger>
      <RightNav open={open} />
    </>
  );
};

const StyledBurger = styled.div`
  width: 21px;
  height: 14px;
  position: fixed;
  position: relative;
  float: right;
  z-index: 60;
  justify-content: space-around;
  flex-flow: column nowrap;
  /* @media (max-width: 768px) {
    display: flex;
    justify-content: space-around;
    flex-flow: column nowrap;
  } */
  cursor: pointer;

  div {
    position: absolute;
    right: 0;
    display: block;
    width: 100%;
    height: 2px;
    background-color: ${({ theme }) => theme.colors.white_color};
    border-radius: 10px;

    transition: all 0.3s linear;

    &:nth-child(1) {
      top: ${({ open }) => (open ? "50%" : "0")};
      margin-top: ${({ open }) => (open ? "-1px" : "0")};
      transform: ${({ open }) => (open ? "rotate(135deg)" : "rotate(0)")};
    }
    &:nth-child(2) {
      /* transform: ${({ open }) => (open ? "translateX(100%)" : "translateX(0)")};
      opacity: ${({ open }) => (open ? "0" : "1")}; */
      width: ${({ open }) => (open ? "0" : "70%")};
      top: ${({ open }) => (open ? "0" : "50%")};
      margin-top: ${({ open }) => (open ? "0" : "-1px")};
      left: ${({ open }) => (open ? "-100%" : "0")};
    }
    &:nth-child(3) {
      bottom: ${({ open }) => (open ? "50%" : "0")};
      margin-bottom: ${({ open }) => (open ? "-1px" : "0")};
      transform: ${({ open }) => (open ? "rotate(-135deg)" : "rotate(0)")};
    }
  }
`;

export default Burger;
