import { useState, useEffect, useRef } from "react";
import styled from "styled-components";

function SectionProject() {
  const text = ["KEEP GOING KEEP GOING"];
  let slides = setSlides();
  const 양끝에_추가될_데이터수 = 2;

  function setSlides() {
    let addedFront = [];
    let addedLast = [];
    var index = 0;
    while (index < 양끝에_추가될_데이터수) {
      addedLast.push(items[index % items.length]);
      addedFront.unshift(items[items.length - 1 - (index % items.length)]);
      index++;
    }
    return [...addedFront, ...items, ...addedLast];
  }

  function replaceSlide(index) {
    setTimeout(() => {
      setTransition("");
      setCurrentIndex(index);
    }, transitionTime);
  }

  return (
    <Container>
      <Wrap>
        <TextBannerWrap
          style={{
            transform: `translateX(${
              (-100 / slides.length) * (0.5 + currentIndex)
            }%)`,
          }}
        >
          {slides.map((text, slideIndex) => {
            const itemIndex = getItemIndex(slideIndex);
            return (
              <div
                key={slideIndex}
                className={`TextBanner ${
                  currentIndex === slideIndex ? "current-slide" : ""
                }`}
                style={{ width: newItemWidth || "auto" }}
              >
                <Text style={{ background: items[itemIndex] }}>
                  {itemIndex}({slideIndex})
                </Text>
              </div>
            );
          })}
        </TextBannerWrap>
      </Wrap>
    </Container>
  );
}

export default SectionProject;

const Container = styled.div`
  width: 100%;
`;

const Wrap = styled.div`
  width: 100%;
`;

const TextBannerWrap = styled.div`
  overflow: hidden;
  width: 100%;
  div {
    white-space: nowrap;
    transition: ease 1000ms;
  }
`;

const Text = styled.div`
  display: inline-block;
  width: 100%;
  font-weight: ${({ theme }) => theme.fonts.weightExtra};
  font-size: 110px;
`;
